void close()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    super();
}

public void init()
{
    super();

    Posting_LedgerDimension.parmIsDefaultAccount(true);

    Posting1_LedgerDimension.parmIsDefaultAccount(true);
}

public boolean lookupLedgerDimension(LedgerJournalACType _acType, FormControl _control)
{
    switch (_acType)
    {
        case LedgerJournalACType::Bank:
            BankAccountTable::lookupBankAccount(_control);
            break;
        case LedgerJournalACType::Cust:
            CustTable::lookupCustomer(_control);
            break;
        case LedgerJournalACType::FixedAssets:
            AssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::Project:
            ProjTable::lookupProjId(_control, rPayCivilContract);
            break;
        case LedgerJournalACType::Vend:
            VendTable::lookupVendor(_control);
            break;
        case LedgerJournalACType::FixedAssets_RU:
            RAssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::RDeferrals:
            RDeferralsTable::lookupDeferrals(_control, dateMax());
            break;
        default:
            return false;
    }
    return true;
}

public boolean useCustomlookupLedgerDimension(int _accountTypeEnumValue)
{
    boolean returnValue;

    LedgerJournalACType accountType = any2Enum(_accountTypeEnumValue);
    switch (accountType)
    {
        case LedgerJournalACType::Bank:
        case LedgerJournalACType::Cust:
        case LedgerJournalACType::FixedAssets:
        case LedgerJournalACType::Project:
        case LedgerJournalACType::Vend:
        case LedgerJournalACType::RDeferrals:
            returnValue = true;
            break;
        default:
            returnValue = false;
            break;
    }

    return returnValue;
}

NumberSeqFormHandler numberSeqFormHandler()
{
    if (prevContractCode != rPayCivilContract.CivilContractTypeCode)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(RPayCivilContractType::find(rPayCivilContract.CivilContractTypeCode).NumberSequenceId,
                                                             element,
                                                             rPayCivilContract_DS,
                                                             fieldNum(RPayCivilContract, ContractId));

        prevContractCode = rPayCivilContract.CivilContractTypeCode;
    }

    return numberSeqFormHandler;
}

void setFields()
{
    DictTable       dictTable = new DictTable(tableNum(RPayCivilContractAct));
    FormDataObject  formDataObject;
    FieldId         fieldId;
    ;

    rPayCivilContractsReverse.enabled(TransLink_RU::exist(rPayCivilContractAct.TableId, rPayCivilContractAct.RecId, rPayCivilContractAct.RecId));

    rPayReverse.enabled(rPayCivilContractAct.PayPeriod < RPayParameters::payPeriod() &&
                        rPayCivilContractAct.Include &&
                        !TransLink_RU::exist(rPayCivilContractAct.TableId, rPayCivilContractAct.RecId, rPayCivilContractAct.RecId));

    rPayCivilContract_DS.allowEdit(! RPayCivilContractAct::existIncluded(rPayCivilContract.ContractId));

    rPayCivilContractAct_DS.allowDelete(! rPayCivilContractAct.Include);

    for (fieldId = dictTable.fieldNext(0); fieldId; fieldId = dictTable.fieldNext(fieldId))
    {
        formDataObject = rPayCivilContractAct_DS.object(fieldId);

        if (formDataObject)
        {
            switch (fieldId)
            {
                case fieldNum(RPayCivilContractAct, Include) :
                    if (rPayCivilContractAct.PayPeriod &&
                        rPayCivilContractAct.PayPeriod < RPayParameters::payPeriod())
                    {
                        formDataObject.allowEdit(false);
                    }
                    else
                    {
                        formDataObject.allowEdit(true);
                    }
                    break;
                default:
                    formDataObject.allowEdit(! rPayCivilContractAct.Include);
            }
        }
    }
}

