public void executeQuery()
{
    if (element.args().dataset() == tableNum(RPayCivilContractAct))
    {
        this.query().dataSourceTable(tableNum(RPayCivilContractAct)).
        addRange(fieldNum(RPayCivilContractAct, RecId)).value(con2Str(TransLink_RU::rpayGetReverseInCon(element.args().record())));
    }

    super();
}

