public void lookup()
{
    SysTableLookup          sysTableLookup  = SysTableLookup::newParameters(tableNum(OfficialsTable_RU), this);
    Query                   query           = new Query();
    QueryBuildDataSource    qbds            = query.addDataSource(tableNum(OfficialsTable_RU));

    qbds.addRange(fieldNum(OfficialsTable_RU, ReportType)).value(queryValue(OffReportType_RU::RPayCivilContract));
    sysTableLookup.addLookupfield(fieldNum(OfficialsTable_RU, Name));
    sysTableLookup.addLookupfield(fieldNum(OfficialsTable_RU, ReprTitleName));
    sysTableLookup.addLookupfield(fieldNum(OfficialsTable_RU, ReprReason));
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    OfficialsTable_RU   officialsTableLocal;
    boolean             ret;

    ret = super();

    if (ret)
    {
        officialsTableLocal = OfficialsTable_RU::findReprName(rPayCivilContract.ReprName);

        if (officialsTableLocal)
        {
            rPayCivilContract.ReprTitleName = officialsTableLocal.ReprTitleName;
            rPayCivilContract.ReprReason    = officialsTableLocal.ReprReason;
        }
    }

    return ret;
}

