void createTree()
{
    RPayStandardDeductionTable      rPayStandardDeductionTable;
    RPayEmplStandardDeductionTable  payEmplStandardDeductionTable;
    FormTreeItem                    childItem, parentItem;
    int                             root, node, i;
    str                             description;
    RHRMEmplTable                   emplTableLocal;
    RHRMPersonTableRecId            personTableRecId;

    tree.deleteAll();

    while select rPayStandardDeductionTable
        order by DeductionOrder
    {
        i = 0;

        personTableRecId = RHRMEmplTable::find(rPayEmplStandardDeductionTable.EmplId).RHRMPersonTable;

        while select DocumentNum, DocumentDate, DeductionCode from payEmplStandardDeductionTable order by DeductionOrder
                  where payEmplStandardDeductionTable.DeductionCode == rPayStandardDeductionTable.DeductionCode
              exists join emplTableLocal
                  where emplTableLocal.EmployeeId      == payEmplStandardDeductionTable.EmplId &&
                        emplTableLocal.RHRMPersonTable == personTableRecId
        {
            if (!i)
            {
                description = "@GLS106063" + ' ' + enum2str(rPayStandardDeductionTable.DeductionType) + '    ' +
                              "@GLS106064" + ' ' + rPayStandardDeductionTable.DeductionCode;

                if (rPayStandardDeductionTable.DeductionName)
                {
                    description += '    ' + "@GLS106065" + ' ' + rPayStandardDeductionTable.DeductionName;
                }
                parentItem = new FormTreeItem(description,0,-1);
                root = tree.addItem(FormTreeAdd::Root,FormTreeAdd::Last,parentItem);
            }

            description = payEmplStandardDeductionTable.DeductionCode;
            if (payEmplStandardDeductionTable.DocumentNum || payEmplStandardDeductionTable.DocumentDate)
            {
                description += "@GLS111192";
                if (payEmplStandardDeductionTable.DocumentNum)
                {
                    description += strfmt("@GLS106066",payEmplStandardDeductionTable.DocumentNum) + '    ';
                }
                if (payEmplStandardDeductionTable.DocumentDate)
                {
                    description += strfmt("@GLS106067",payEmplStandardDeductionTable.DocumentDate);
                }
            }

            parentItem.children(parentItem.children() + 1);
            childItem = new FormTreeItem(description,1,0);

            node = tree.addItem(root,node,childItem);
            i++;
        }
    }
}

public boolean lookupLedgerDimension(LedgerJournalACType _acType, FormControl _control)
{
    switch (_acType)
    {
        case LedgerJournalACType::Bank:
            BankAccountTable::lookupBankAccount(_control);
            break;
        case LedgerJournalACType::Cust:
            CustTable::lookupCustomer(_control);
            break;
        case LedgerJournalACType::FixedAssets:
            AssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::Project:
            ProjTable::lookupProjId(_control, RPayEmplStandardDeductionTable);
            break;
        case LedgerJournalACType::Vend:
            VendTable::lookupVendor(_control);
            break;
        case LedgerJournalACType::RDeferrals:
            RDeferralsTable::lookupDeferrals(_control, dateMax());
            break;
        case LedgerJournalACType::FixedAssets_RU:
            RAssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::RCash:
            RCashTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::Employee_RU:
            EmployeeTable_RU::lookupAdvHolder(_control);
            break;
        default:
            return false;
    }
    return true;
}

public boolean useCustomlookupLedgerDimension(int _accountTypeEnumValue)
{
    boolean returnValue;

    LedgerJournalACType accountType = any2Enum(_accountTypeEnumValue);
    switch (accountType)
    {
        case LedgerJournalACType::Bank:
        case LedgerJournalACType::Cust:
        case LedgerJournalACType::FixedAssets:
        case LedgerJournalACType::Project:
        case LedgerJournalACType::Vend:
        case LedgerJournalACType::RDeferrals:
        case LedgerJournalACType::FixedAssets_RU:
        case LedgerJournalACType::RCash:
        case LedgerJournalACType::Employee_RU:
            returnValue = true;
            break;
        default:
            returnValue = false;
            break;
    }

    return returnValue;
}

