display RPayAmount baseAmount()
{
    return baseAmount;
}

public void clearTotals()
{
    baseAmount = 0;
    fundAmount = 0;
    rPayFundTotalAmount = 0;
    deductionAmount = 0;
    fundAmount = 0;
    fundAmount = 0;
    fundAmount = 0;
}

display RPayAmount deductionAmount()
{
    return deductionAmount;
}

public void enableButtonsActive()
{
    Yr  taxYear = year(FundPeriodControl.dateValue());

    #define.NewPersonalCardReportYear(2010)

    RPayPersonalCardReport_2010.visible(taxYear >= #NewPersonalCardReportYear);
}

display RPayAmount fundAmount()
{
    return fundAmount;
}

void init()
{
    Common common = element.args().record();

    super();
    fundPeriodControl.dateValue(dateEndYr(RPayParameters::payPeriod()));
    rpayTaxSumCalc = new RpayTaxSumCalc();
    groupEmpl.widthValue(300);

    if (common.TableId == tablenum(RHRMEmplTable))
    {
        all.enabled(false);
        tabEmpl.visible(false);
        groupEmpl.widthValue(65);
        ctrlSplitGorizontal.visible(false);
    }

    element.enableButtonsActive();
    this.arrange();
}

display RPayAmount rPayFundTotalAmount()
{
    return rPayFundTotalAmount;
}

void updateTotals()
{
    container fundSum;

    fundSum = RpayFundEmplSum::getFundSum(rPayFundEmplSum.EmplId,
                                          rPayFundEmplSum.RpayCounterUnit,
                                          mkdate(1,1,year(rPayFundEmplSum.PayPeriod)),
                                          rPayFundEmplSum.PayPeriod);

    baseAmount          = conpeek(fundSum,1);
    fundAmount          = conpeek(fundSum,2);
    rPayFundTotalAmount = conpeek(fundSum,3);
    deductionAmount     = RpayFundEmplDeduction::getFundTotalDeductionAmount(rPayFundEmplSum.EmplId,
                                                                             rPayFundEmplSum.RpayCounterUnit,
                                                                             mkdate(1,1,year(rPayFundEmplSum.PayPeriod)),
                                                                             rPayFundEmplSum.PayPeriod);

    isFundPercent = RPayCounterTable::find(rPayFundEmplSum.RpayCounterUnit).PayRatePercent != '';
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.clearTotals();
    }

    return ret;
}

public void executeQuery()
{
    if (element.args().dataset() == tableNum(RHRMEmplTable))
    {
        findOrCreateRange_W(this.query().dataSourceTable(tableNum(RHRMEmplTable)), fieldNum(RHRMEmplTable, PayMainEmplId)).value('(EmplTable.PayMainEmplId = EmplTable.PayMainEmplId)');
    }
    else
    {
        findOrCreateRange_W(this.query().dataSourceTable(tableNum(RHRMEmplTable)), fieldNum(RHRMEmplTable, PayMainEmplId)).value('(EmplTable.PayMainEmplId = EmplTable.EmployeeId)');
    }

    super();
}

public int active()
{
    int ret;

    ret = super();
    element.updateTotals();
    TabPageTotal.visible(isFundPercent);
    return ret;
}

display  RPayAmount baseTotal(RpayFundEmplSum rpayFundEmplSumds)
{
    return  RpayFundEmplSum::getFundBaseAmount(rpayFundEmplSumds.EmplId,
                                               rpayFundEmplSumds.RpayCounterUnit,
                                               endmth(dateStartYr(fundPeriodControl.dateValue())),
                                               dateEndYr(fundPeriodControl.dateValue()));
}

display RPayAmount currentTotal(RpayFundEmplSum rpayFundEmplSumds)
{
    return  RpayFundEmplSum::getFundAmount(rpayFundEmplSumds.EmplId,
                                           rpayFundEmplSumds.RpayCounterUnit,
                                           endmth(dateStartYr(fundPeriodControl.dateValue())),
                                           dateEndYr(fundPeriodControl.dateValue()));
}

display  RPayAmount allTotal(RpayFundEmplSum rpayFundEmplSumds)
{
    return  RpayFundEmplSum::getAllFundAmount(rpayFundEmplSumds.EmplId,
                                              endmth(dateStartYr(fundPeriodControl.dateValue())),
                                              dateEndYr(fundPeriodControl.dateValue()));
}

void executeQuery()
{
    criteriaOpen.value(SysQuery::range(endmth(dateStartYr(fundPeriodControl.dateValue())), dateEndYr(fundPeriodControl.dateValue())));

    super();
}

void init()
{
    QueryBuildDataSource qbds;
    super();

    this.cacheAddMethod(tablemethodstr(RpayFundEmplSum, fundName));

    criteriaOpen = this.query().dataSourceNo(1).addRange(fieldnum(RpayFundEmplSum, PayPeriod));

    qbds = this.query().dataSourceTable(tableNum(RpayFundEmplSum));
    qbds.clearDynalinks();
    qbds.addDynalink(fieldnum(RpayFundEmplSum, EmplId), EmplTable, fieldNum(RHRMEmplTable, PayMainEmplId));
}

public void init()
{
    super();

    this.cacheAddMethod(tablemethodstr(RpayFundEmplDeduction, deductionName));
}

public void executeQuery()
{
    if (!isFundPercent)
    {
        return;
    }

    criteriaOpenTotal.value(SysQuery::range(endmth(dateStartYr(fundPeriodControl.dateValue())), dateEndYr(fundPeriodControl.dateValue())));
    criteriaEmplId.value(EmplTable.PayMainEmplId);
    criteriaCounterUnit.value(RpayFundEmplSum.RPayCounterUnit);

    super();
}

public void init()
{
    QueryBuildDatasource    qbds;

    super();
    qbds = this.query().dataSourceName(this.name());

    criteriaOpenTotal   = qbds.addRange(fieldnum(RpayFundEmplSum, PayPeriod));
    criteriaEmplId      = qbds.addRange(fieldnum(RpayFundEmplSum, EmplId));
    criteriaCounterUnit = qbds.addRange(fieldnum(RpayFundEmplSum, RPayCounterUnit));

    qbds.addSelectionField(fieldnum(RpayFundEmplSum, PercentValue));
    qbds.addSelectionField(fieldnum(RpayFundEmplSum, BaseAmount), SelectionField::Sum);
    qbds.addSelectionField(fieldnum(RpayFundEmplSum, FundAmount), SelectionField::Sum);
    qbds.addSortField(fieldnum(RpayFundEmplSum, PercentValue));
    qbds.addSortField(fieldnum(RpayFundEmplSum, RPayCounterUnit));
    qbds.orderMode(OrderMode::GroupBy);
}

public int active()
{
    int ret;

    ret = super();

    RPayFundEmplSumTotalAll_DS.object(fieldnum(RPayFundEmplSumTotal, IncludeExtYear)).allowEdit(RPayFundEmplSumTotalAll.VendAccount != '');
    RPayFundEmplSumTotalAll_ds.allowDelete(RPayFundEmplSumTotalAll.IsManual);

    return ret;
}

#define.dateNull(1900)
public boolean validateWrite()
{
    boolean ret;
    ret = super();

    if (!RPayFundEmplSumTotalAll.vendAccount && RPayFundEmplSumTotalAll.IsManual)
    {
        ret = checkFailed("@RUP3288");
    }

    if (RPayFundEmplSumTotalAll.Yr < #dateNull)
    {
        ret = checkFailed("@RUP3287");
    }

    return ret;
}

public void initValue()
{
    super();
    RPayFundEmplSumTotalAll.Include  = NoYes::Yes;
    RPayFundEmplSumTotalAll.IsManual = NoYes::Yes;
}

public void modified()
{
    super();

    if (RPayFundEmplSumTotalAll.VendAccount)
    {
        RPayFundEmplSumTotalAll_DS.object(fieldnum(RPayFundEmplSumTotal, IncludeExtYear)).allowEdit(true);
    }
    else
    {
        RPayFundEmplSumTotalAll.IncludeExtYear = NoYes::No;
        RPayFundEmplSumTotalAll_DS.object(fieldnum(RPayFundEmplSumTotal, IncludeExtYear)).allowEdit(false);
    }
}

public boolean validate()
{
    boolean ret;

    if ( !RPayFundEmplSumTotalAll.IsManual)
    {
        return false;
    }

    ret = super();

    return ret;
}

void clicked()
{
    rpayTaxSumCalc.updateFundPeriod(
        endmth(dateStartYr(fundPeriodControl.dateValue())),
        emplTable.PayMainEmplId,
        RPayParameters::payPeriod(emplTable.PayMainEmplId));

    rpayFundEmplSum_Ds.research();
    RPayFundEmplSumTotalAll_Ds.research();
    super();
}

void clicked()
{
    super();
    rpayTaxSumCalc.updateFundPeriod(
        endmth(dateStartYr(fundPeriodControl.dateValue())),
        '',
        RPayParameters::payPeriod(emplTable.PayMainEmplId));

    rpayFundEmplSum_Ds.research();
    RPayFundEmplSumTotalAll_Ds.research();
}

boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        TabPageTotal.visible(false);
        this.dateValue(dateEndYr(this.dateValue()));
        rpayFundEmplSum_ds.executeQuery();
        element.enableButtonsActive();
    }

    return ret;
}
