Method.Source:
[Form]
public class RPayParameters extends FormRun
{
    NumberSeqReference      numberSeqReference;
    boolean                 runExecuteDirect;

    TmpIdRef                    tmpIdRef;
    NumberSeqApplicationModule  numberSeqApplicationModule;
    container                   numberSequenceModules;
    NumberSeqScope             scope;
}

    void init()
{
    this.numberSeqPreInit();

    super();

    RPayParameters::find();

    this.numberSeqPostInit();
}

    public void numberSeqPostInit()
{
    numberSequenceReference_ds.object(fieldNum(NumberSequenceReference, AllowSameAs)).visible(numberSeqApplicationModule.sameAsActive());
    ctrlLabelSameAs.visible(numberSeqApplicationModule.sameAsActive());
}

    void numberSeqPreInit()
{
    runExecuteDirect   = false;

    numberSequenceModules = [NumberSeqModule::RPayroll];
    numberSeqApplicationModule = new NumberSeqModuleRPayroll();
    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

    void setHelpForPayLevelRoll()
{
    str sHelp;
    ;
    switch (payParameters.PayLevelRoll)
    {
        case RPayLevelRoll::RollAll :
            sHelp = "@GLS105744";
            break;
        case RPayLevelRoll::SeparateEmplId :
            sHelp = "@GLS105741";
            break;
        case RPayLevelRoll::SeparateAll :
            sHelp = "@GLS105743";
            break;
        default :
            sHelp = "";
    }
    payLevelRoll_Help.text(sHelp);
}

    public void updateDesign()
{
    boolean isNextEnabled()
    {
        return !(payParameters.CalcDate
            &&  (RPayCalcSalaryPeriodicity::isLastInPayPeriod(payParameters.CalcDate, payParameters.PayPeriod)
            ||  (select firstonly rPayModuleTable where rPayModuleTable.PayModuleSost == RPayModuleSost::NoCalculate).RecId
            ||  (select firstonly rPayEmplCalculate).RecId
            ||  ! RPayCalcSalaryPeriodicity::findNext(payParameters.CalcDate).CalcDate
            ||  endmth(RPayCalcSalaryPeriodicity::findNext(payParameters.CalcDate).CalcDate) != payParameters.PayPeriod));
    }

    boolean isPrevEnabled()
    {
        return !(!payParameters.CalcDate
            || RPayCalcSalaryPeriodicity::find(payParameters.CalcDate).IsPaid
            || RPayCalcSalaryPeriodicity::isFirstInPayPeriod(payParameters.CalcDate, payParameters.PayPeriod));
    }

    calcDatePrevButton.enabled(isPrevEnabled());
    calcDateNextButton.enabled(isNextEnabled());
}

        int active()
{
    int ret;

    ret = super();

    element.setHelpForPayLevelRoll();

    element.updateDesign();

    return ret;
}

            public void modified()
{
    super();
    element.updateDesign();
}

            public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = RPayController::canPayLevelRollChange(payParameters);
    }

    return ret;
}

            public void modified()
{
    super();
    element.setHelpForPayLevelRoll();
}

            public boolean validate()
{
    boolean ret;

    ret = super();
    if (ret) ret = RPayController::canPayPeriodChange(payParameters);
    return ret;
}

            public void modified()
{
    super();
    RPayController::payPeriodChanged(payParameters);
    payParameters_ds.refresh();
    element.updateDesign();
}

        void executeQuery()
{
    if (runExecuteDirect)
    {
        super();
    }
    else
    {
        runExecuteDirect = true;
        this.queryRun(NumberSeqReference::buildQueryRunMulti(numberSequenceReference,
                                                             tmpIdRef,
                                                             numberSequenceTable,
                                                             numberSequenceModules,
                                                             scope));
        numbersequenceReference_ds.research();
    }
}

        void removeFilter()
{
    runExecuteDirect = false;
    numbersequenceReference_ds.executeQuery();
}

            public Common resolveReference(FormReferenceControl _formReferenceControl)
{
     NumberSequenceCode code = _formReferenceControl.filterValue(
        AbsoluteFieldBinding::construct(fieldStr(NumberSequenceTable, NumberSequence),
        tableStr(NumberSequenceTable))).value();

    // Do not call super as we're providing our own disambiguation logic.
    // resolvedRecord = super(_formReferenceControl);

    return NumberSequenceTable::findByNaturalKey(code, scope.getId(true));
}

            public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    NumberSequenceTable     selectedRecord;
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(NumberSequenceTable), _formReferenceControl, true);
    Query lookupQuery;

    // Do not call super as we're providing our own custom lookup logic.
    // selectedRecord = super(_formReferenceControl);

    // Display the Title and Department fields in the lookup form.
    sysTableLookup.addLookupfield(fieldNum(NumberSequenceTable, NumberSequence));

    // Create a custom Query that filters on NumberSequenceScope.
    lookupQuery = new Query();
    lookupQuery.addDataSource(tableNum(NumberSequenceTable)).addRange(fieldNum(NumberSequenceTable, NumberSequenceScope)).value(queryValue(scope.getId(true)));
    sysTableLookup.parmQuery(lookupQuery);

    // Return the record selected by the user.
    selectedRecord = sysTableLookup.performFormLookup();

    return selectedRecord;
}

        public void delete()
{
    RPayParameters rPayParameters;

    if (rPayCalcSalaryPeriodicity.CalcDate == RPayParameters::calcDate())
    {
        ttsbegin;
        rPayParameters = RPayParameters::find(true);
        rPayParameters.CalcDate = dateNull();
        rPayParameters.doUpdate();
        ttscommit;
    }

    super();

    payParameters_ds.reread();
    payParameters_ds.refresh();
    element.updateDesign();
}

        public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        rPayCalcSalaryPeriodicity_ds.allowDelete(! rPayCalcSalaryPeriodicity.IsPaid                                &&
                                                 (rPayCalcSalaryPeriodicity.CalcDate != RPayParameters::calcDate() ||
                                                 (rPayCalcSalaryPeriodicity.CalcDate == RPayParameters::calcDate() &&
                                                  ! RPayCalcSalaryPeriodicity::findNext(rPayCalcSalaryPeriodicity.CalcDate))));
    }

    return ret;
}

        void clicked()
{
    super();

    if (RPayController::canCalcDateChange(payParameters, NextPrevious::Previous))
    {
        payParameters.CalcDate = RPayCalcSalaryPeriodicity::findPrev(payParameters.CalcDate).CalcDate;

        RPayController::calcDateChanged(payParameters.orig(), payParameters.CalcDate);
        payParameters_DS.refresh();
    }

    element.updateDesign();
}

        void clicked()
{
    super();

    if (RPayController::canCalcDateChange(payParameters, NextPrevious::Next))
    {
        if (payParameters.CalcDate)
        {
            payParameters.CalcDate = RPayCalcSalaryPeriodicity::findNext(payParameters.CalcDate).CalcDate;
        }
        else
        {
            payParameters.CalcDate = RPayCalcSalaryPeriodicity::findFirstInPayPeriod(payParameters.PayPeriod).CalcDate;
        }

        RPayController::calcDateChanged(payParameters.orig(), payParameters.CalcDate);
        payParameters_DS.refresh();
    }

    element.updateDesign();
}
