Method.Source:
    [Form]
    public class RPaySheetBankPayment extends FormRun
    {
        #resAppl
        QueryBuildRange     showSheetTable;
        RPayStatusRButton   RPayStatusRButton;
}

void init()
{
    Common  common = element.args().record();
    RPaySheetTable  localrpaySheetTable;

    super();

    if (common.TableId == tablenum(RPaySheetTable))
    {
        localrpaySheetTable = common;
        switch (localrpaySheetTable.PaySheetSost)
        {
            case RPaySheetSost::ForPaid:
                RPayStatusRButton = RPayStatusRButton::ForPaid;
                break;
            case RPaySheetSost::Paid:
                RPayStatusRButton = RPayStatusRButton::Paid;
                break;
        }
    }
}

void refreshForm()
{
    RPaySheetTransBank_ds.research();
}

void refreshRPaySheetTable()
{
    RPaySheetTable_ds.reread();
    RPaySheetTable_ds.refresh();
}

void run()
{
    super();

    showAll.selection(RPayStatusRButton);
    RPaySheetTable_ds.executeQuery();
    if (!RPaySheetTable.RecId && showAll.selection() != RPayStatusRButton::All)
    {
        showAll.selection(RPayStatusRButton::All);
        RPaySheetTable_ds.executeQuery();
    }
}

void setAccess()
{
    RPaySheetTable_ds.allowEdit(RPaySheetTable.PaySheetSost != RPaySheetSost::Paid);

    deleteBankPayment.enabled(! ledgerJournalTable.Posted && RPaySheetTransBank.JournalNum ? true : false);
    postBankJournal.enabled(! ledgerJournalTable.Posted && RPaySheetTransBank.JournalNum ? true : false);
}

int active()
{
    int ret;

    ret = super();

    element.setAccess();

    return ret;
}

void executeQuery()
{
    if (showAll.selection() == RPayStatusRButton::All)
    {
        showSheetTable.value('!' + enum2str(RPaySheetSost::NoPaid));
    }
    else if (showAll.selection() ==  RPayStatusRButton::Paid)
    {
        showSheetTable.value(enum2str(RPaySheetSost::Paid));
    }
    else
    {
        showSheetTable.value(enum2str(RPaySheetSost::ForPaid));
    }
    super();
}

void init()
{
    super();

    this.cacheAddMethod(tablemethodstr(RPaySheetTable, checkIfLines));
    this.cacheAddMethod(tablemethodstr(RPaySheetTable, payBankSum));
    this.cacheAddMethod(tablemethodstr(RPaySheetTable, payBankSumInJournal));
    this.cacheAddMethod(tablemethodstr(RPaySheetTable, payBankSumPosted));

    this.query().dataSourceNo(1).addRange(fieldnum(RPaySheetTable, BankPayment)).value(enum2str(NoYes::Yes));
    showSheetTable = this.query().dataSourceNo(1).addRange(fieldnum(RPaySheetTable, PaySheetSost));
}

int active()
{
    int ret;

    ret = super();

    element.setAccess();

    return ret;
}

void delete()
{
    RPaySheetTrans  localRPaySheetTrans;

    if (! RPaySheetTable.checkUpdate())
    {
        return;
    }

    ttsbegin;

    for (localRPaySheetTrans = RPaySheetTransBank_ds.getFirst(true) ? RPaySheetTransBank_ds.getFirst(true) : RPaySheetTransBank_ds.cursor();
         localRPaySheetTrans;
         localRPaySheetTrans = RPaySheetTransBank_ds.getNext())
    {
        localRPaySheetTrans = RPaySheetTrans::find(localRPaySheetTrans.Emplid, localRPaySheetTrans.SheetTableRecId,true);

        if (localRPaySheetTrans.JournalNum)
        {
            continue;
        }

        localRPaySheetTrans.SendToBank = NoYes::No;
        localRPaySheetTrans.update();
        if (! localRPaySheetTrans.checkLastRecordBankPayment())
        {
            RPaySheetTable.BankPayment = NoYes::No;
            RPaySheetTable.update();
        }
        if (RPaySheetTable.CashOrderNum)
        {
            localRPaySheetTrans.BankPayment   = NoYes::No;
            localRPaySheetTrans.Deposit       = NoYes::Yes;
            localRPaySheetTrans.SendToDeposit = NoYes::Yes;
            localRPaySheetTrans.update();
        }
    }

    if (RPaySheetTable.CashOrderNum)
    {
        RPaySheetTable.createPayDeposit();
    }

    ttscommit;

    rpaySheetTransBank_ds.executeQuery();
}

void init()
{
    super();

    this.cacheAddMethod(tablemethodstr(RPaySheetTrans, checkIfLines));
    this.cacheAddMethod(tablemethodstr(RPaySheetTrans, postedBank));
    this.cacheAddMethod(tablemethodstr(RPaySheetTrans, getShortEmplName));

    this.query().dataSourceNo(1).addRange(fieldnum(RPaySheetTrans, SendToBank)).value(enum2str(NoYes::Yes));
}

void clicked()
{
    RPayDepositTable    RPayDepositTable;

    bankJournal.enabled(RPaySheetTransBank.JournalNum && header.tab() == 2);

    select firstonly RecId from RPayDepositTable
        where RPayDepositTable.RefPaySheetTableRecId == RPaySheetTable.RecId;

    depositJournal.enabled(RPayDepositTable.RecId);

    super();
}

void clicked()
{
    Args args = new Args();
    RPaySheetTrans paySheetTransBank;

    if (rPaySheetTransBank.postedBank())
    {
        super();
    }
    else
    {
        select paySheetTransBank
            where paySheetTransBank.SheetTableRecId == rPaySheetTransBank.SheetTableRecId &&
                  paySheetTransBank.Emplid == rPaySheetTransBank.Emplid;

        args.record(paySheetTransBank);

        new MenuFunction(menuitemdisplaystr(LedgerJournalTable3), MenuItemType::Display).run(args);
    }
}

void clicked()
{
    RPaySheetTrans    rPaySheetTransLocal;
    RPaySheetTable    rPaySheetTableLocal;
    boolean           state;

    if (header.tab() == 1)
    {
        for (rPaySheetTableLocal = rPaySheetTable_ds.getFirst(true) ? rPaySheetTable_ds.getFirst(true) : rPaySheetTable_ds.cursor();
             rPaySheetTableLocal;
             rPaySheetTableLocal = rPaySheetTable_ds.getNext())
        {
            select firstonly RecId from rPaySheetTransLocal
                where rPaySheetTransLocal.SheetTableRecId == rPaySheetTableLocal.RecId &&
                      rPaySheetTransLocal.SendToBank      == NoYes::Yes                &&
                      ! rPaySheetTransLocal.JournalNum;
            if (rPaySheetTransLocal.RecId)
            {
                state = true;
                break;
            }
        }
    }

    else if (header.tab() == 2)
    {
        for (rPaySheetTransLocal = RPaySheetTransBank_ds.getFirst(true) ? RPaySheetTransBank_ds.getFirst(true) : RPaySheetTransBank_ds.cursor();
             rPaySheetTransLocal;
             rPaySheetTransLocal = RPaySheetTransBank_ds.getNext())
        {
            if (! rPaySheetTransLocal.JournalNum)
            {
                state = true;
                break;
            }
        }
    }

    createBankPayment.enabled(state);

    super();
}

void clicked()
{
    Args    args;
    args = new Args();
    args.caller(element);

    if (header.tab() == 1)
        args.record(RPaySheetTable);
    if (header.tab() == 2)
        args.record(RPaySheetTransBank);

    new MenuFunction(menuitemactionstr(RPayBankCreateJournal), MenuItemType::Action).run(args);

    element.refreshRPaySheetTable();
}

void clicked()
{
    LedgerJournalTrans      ledgerJournalTrans;
    ledgerJournalTable      localLedgerJournalTable;
    int64                   countTrans;
    int                     i;
    RPaySheetTrans          rPaySheetTransQ;
    container               con, conItem;
    RPaySheetTable          rPaySheetTableLocal;
    RPayAmount              amount;

    select count(RecId) from rPaySheetTransQ
        where    rPaySheetTransQ.JournalNum == rPaySheetTransBank.JournalNum;

    countTrans = rPaySheetTransQ.RecId;

    while select sum(Amount) from rPaySheetTransQ
        group by SheetTableRecId
        where rPaySheetTransQ.JournalNum == rPaySheetTransBank.JournalNum
    {
        conItem =  [rPaySheetTransQ.SheetTableRecId, rPaySheetTransQ.Amount];
        con     += [conItem];
    }
    ttsbegin;

    localLedgerJournalTable = LedgerJournalTable::find(rPaySheetTransBank.JournalNum, true);

    while select forupdate ledgerJournalTrans
        where ledgerJournalTrans.JournalNum == rPaySheetTransBank.JournalNum
    {
        if (ledgerJournalTrans.validateDelete())
        {
            ledgerJournalTrans.delete();
        }
    }

    if (localLedgerJournalTable.validateDelete())
    {
        localLedgerJournalTable.delete();

        for (i = 1; i <= conlen(con); i ++)
        {
            conItem             = conpeek(con, i);
            rPaySheetTableLocal = RPaySheetTable::find(conpeek(conItem, 1), true);
            amount              = conpeek(conItem, 2);
            rPaySheetTableLocal.updateDisbursedSum(- amount);
        }

        ttscommit;
        info("@SYS13435");
    }
    else
    {
        ttsabort;
    }

    if (countTrans > 1)
    {
        rPaySheetTransBank_ds.executeQuery();
    }
    else
    {
        rPaySheetTransBank_ds.reread();
        rPaySheetTransBank_ds.refresh();
        element.setAccess();
    }

    element.refreshRPaySheetTable();
}

