Method.Source:
    [Form]
    public class RPaySickListParameters extends FormRun
    {
        Integer                 lastSel;
        QueryBuildRange         criteriaOpen;
}

void init()
{
    RPayAverageEarnTable    averageEarnTable;

    super();

    RPayAverageEarnParameters::find();

    select firstonly averageEarnTable
        where averageEarnTable.RpayAverageEarnCodeType == RPayAverageEarnCodeType::SickList;

    if (! averageEarnTable)
    {
        ttsbegin;
        averageEarnTable.RpayAverageEarnCodeType = RPayAverageEarnCodeType::SickList;
        averageEarnTable.rPayAverageEarnCode     = new DictEnum(enumNum(RPayAverageEarnCodeType)).index2Symbol(RPayAverageEarnCodeType::SickList);
        averageEarnTable.insert();
        ttscommit;
    }
}

void updateDesign()
{
    boolean visible = (rPayAverageEarnTable.RpaySiclListType == RPaySickListType::Fact);
    ;
    monthForCalc.visible(visible);
    calcByFund.visible(visible);
    averageFund.visible(visible);
    riseFactor.visible(visible);
    averageTimeGroupCode.visible(visible);
    groupBonus.visible(visible);
}

int active()
{
    int     ret;
    boolean isChildVacation;

    ret = super();

    if (paySickIllParameters.DaysRestrictionRate)
    {
        daysRestriction_PercentRate.allowEdit(true);
    }
    else
    {
        daysRestriction_PercentRate.allowEdit(false);
    }
    isChildVacation = paySickIllParameters.PayIllType == RPayIllType::ChildVacation;
    ageLimit.visible(isChildVacation);
    paySickIllParameters_DS.object(fieldNum(RPaySickIllParameters, AgeLimitFSS)).visible(isChildVacation);
    paySickIllParameters_DS.object(fieldNum(RPaySickIllParameters, ExcludeSeniorityTypeId)).visible(isChildVacation);
    paySickIllParameters_DS.object(fieldNum(RPaySickIllParameters, ChildAllowancePersent)).visible(isChildVacation);
    paySickIllParameters_DS.object(fieldNum(RPaySickIllParameters, ChildAllowancePersentFSS)).visible(isChildVacation);
    paySickIllParameters_DS.object(fieldNum(RPaySickIllParameters, ChildMinPerNumber)).visible(isChildVacation);
    paySickIllParameters_DS.object(fieldNum(RPaySickIllParameters, ChildMinPerNumberFSS)).visible(isChildVacation);
    paySickIllParameters_DS.object(fieldNum(RPaySickIllParameters, AverageMonthLength)).visible(isChildVacation);
    paySickIllParameters_DS.object(fieldNum(RPaySickIllParameters, ChildAECriteria)).visible(isChildVacation);

    return ret;
}

public void jumpRef()
{
    Args args = new Args();
    args.record(paySickIllParameters);

    new MenuFunction(menuitemDisplayStr(RPaySickListParameters), MenuItemType::Display).run(args);
}

void modified()
{
    super();

    if (paySickIllParameters.DaysRestrictionRate)
    {
        daysRestriction_PercentRate.allowEdit(true);
    }
    else
    {
        paySickIllParameters.PercentRate = '';
        daysRestriction_PercentRate.allowEdit(false);
    }
}

public void initValue()
{
    ;
    super();
    rpayAverageEarnTable.RpaySiclListType = RPaySickListType::Fact;
}

public int active()
{
    int ret;

    ret = super();
    if (ret)
    {
        element.updateDesign();
    }
    return ret;
}

int selectionChange()
{
    int  ret;
    ;
    if (! paySickIllParameters_ds.leaveRecord())
    {
        this.selection(lastSel);
        return 0;
    }

    ret = super();

    paySickIllParameters_ds.executeQuery();

    return ret;
}

public void lookup()
{
    ;
    if (paySickIllParameters.PayIllType == RPayIllType::ChildVacation)
    {
        RPaySeqTable::lookupModuleSeq(this,'RPayEmplPeriod');
    }
    else
    {
        RPaySeqTable::lookupModuleSeq(this,'RPaySickList');
    }
}
