void initValue()
{
    super();
    payTrans.TransDate = endmth(periodDateControl.dateValue());
    payTrans.initFromEmplTable(emplTable);
}

void executeQuery()
{
    this.updateCriteria();

    super();
}

void init()
{
    super();

    this.cacheAddMethod(tablemethodstr(RPayTrans, costTypeName));

    criteriaOpen            = this.query().dataSourceNo(1).addRange(fieldnum(RPayTrans, PayPeriod));
    criteriaEmplId          = this.query().dataSourceNo(1).addRange(fieldnum(RPayTrans, Emplid));
}

display Amount sumNach(RPayTrans _payTransds)
{
    return RPayTrans::getSumAmount(_payTransds.PayPeriod, _payTransds.Emplid, byWorker.value(), RPayChargeType::AddSum);
}

display Amount sumOnHand(RPayTrans _payTransds)
{
    return this.sumNach(_payTransds) - this.sumUder(_payTransds);
}

display Amount sumUder(RPayTrans _payTransds)
{
    return  -RPayTrans::getSumAmount(_payTransds.payPeriod, _payTransds.EmplId, byWorker.value(), RPayChargeType::Deduction);
}

public void updateCriteria()
{
    str             emplIds;
    RHRMEmplTable   localEmplTable;

    criteriaOpen.value(queryValue(endmth(periodDateControl.dateValue())));
    criteriaEmplId.value("");

    if (byWorker.value())
    {
        while select employeeId from localEmplTable
            where localEmplTable.RHRMPersonTable == emplTable.RHRMPersonTable
        {
            emplIds += localEmplTable.employeeId + ", ";
        }
    }
    else
    {
        emplIds = emplTable.EmployeeId;
    }
    criteriaEmplId.value(emplIds);
}

public boolean validate()
{
    boolean     ret;

    ret = super();

    if (ret)
    {
        if (byWorker.value() ?
             empltable.personTable().RecId != RHRMEmplTable::find(PayTrans.Emplid).personTable().RecId :
             emplTable.EmployeeId != PayTrans.Emplid
          )
        {
            ret = checkFailed("@GLS106221");
        }
    }

    return ret;
}

public void modified()
{
    payTrans.initFromEmplTable(RHRMEmplTable::find(payTrans.Emplid));
}

public void modified()
{
    super();
    payTrans_ds.cacheCalculateMethod(tablemethodstr(RPayTrans, costTypeName));
}

void clicked()
{
    RHRMEmplTable               emplTableLocal;
    RPayModuleCalculate         payModuleCalculate;

    if (byWorker.value())
    {
        while select EmployeeId from emplTableLocal
            where emplTableLocal.RHRMPersonTable == emplTable.RHRMPersonTable
        {
            payModuleCalculate = new RPayModuleCalculate(emplTable.EmployeeId);
            payModuleCalculate.calculateAllModule();
        }
    }
    else
    {
        payModuleCalculate = new RPayModuleCalculate(emplTable.EmployeeId);
        payModuleCalculate.calculateAllModule();
    }

    payTrans_Ds.research();
    super();
}

