public class RPaySumEmpl extends FormRun
{
    QueryBuildRange         criteriaOpen, criteriaEmplId, criteriaSourceTableId, criteriaSourceRecId;
    RHRMEmplId              currentEmplId;
    boolean                 updateDesign, viewMode;
    RefRecId    sourceRecId;
    TableId     sourceTableId;
}

void enableButtonsActive()
{
    TransLink_RU tranLink;
    boolean      enabled;

    select tranLink
        where tranLink.RefTableId == payTrans.TableId &&
              tranLink.RefPri     == payTrans.RecId   &&
              tranLink.RefSec     == payTrans.RecId;

    enabled = (! viewMode                                       &&
               payTrans.PayPeriod < RPayParameters::payPeriod(currentEmplId ? currentEmplId : payTrans.Emplid) &&
               !payTrans.SourceTableId                          &&
               !payTrans.SourceRecId                            &&
               !tranLink);

    rPayReverse.enabled(enabled);
    rPayReverse_Correct.enabled(enabled);
    rPayReverse_ReverseCorrect.enabled(enabled);
}

void init()
{
    ReportPeriod_RU payPeriod;
    Object  o;
    EmplTransSource_Payroll_RU  payrollSource;

    if (element.args() && element.args().caller())
    {
        o = element.args().caller();
        viewMode = o is RTax25OriginalDocuments;

        if (o is EmplTransSource_Payroll_RU)
        {
            payrollSource = o;
            sourceRecId = payrollSource.getSourceRecId();
            sourceTableId = payrollSource.getSourceTableId();
        }
    }

    element.initEmplTable();

    payPeriod = RPayParameters::payPeriod(currentEmplId);

    super();

    periodDateControl.dateValue(payPeriod != endmth(dateNull()) ? payPeriod : dateNull());

    if (viewMode)
    {
        payTrans_DS.allowDelete(false);
        payTrans_DS.allowEdit(false);
        payTrans_DS.allowCreate(false);
        periodDateControl.visible(false);
        buttonCalculation.visible(false);
    }
}

void initEmplTable()
{
    RHRMEmplTable emplTable;
    RPayTrans     rpayTrans;

    if (element.args().dataset() == tableNum(RHRMEmplTable))
    {
        emplTable.data(element.args().record());
        currentEmplId = emplTable.EmployeeId;
    }

    if (element.args().dataset() == tableNum(RPayTrans))
    {
        rpayTrans.data(element.args().record());
        currentEmplId = rpayTrans.Emplid;
    }
}

public boolean lookupLedgerDimension(LedgerJournalACType _acType, FormControl _control)
{
    switch (_acType)
    {
        case LedgerJournalACType::Bank:
            BankAccountTable::lookupBankAccount(_control);
            break;
        case LedgerJournalACType::Cust:
            CustTable::lookupCustomer(_control);
            break;
        case LedgerJournalACType::FixedAssets:
            AssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::Project:
            ProjTable::lookupProjId(_control, PayTrans);
            break;
        case LedgerJournalACType::Vend:
            VendTable::lookupVendor(_control);
            break;
        case LedgerJournalACType::RDeferrals:
            RDeferralsTable::lookupDeferrals(_control, dateMax());
            break;
        case LedgerJournalACType::FixedAssets_RU:
            RAssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::RCash:
            RCashTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::Employee_RU:
            EmployeeTable_RU::lookupAdvHolder(_control);
            break;
        default:
            return false;
    }
    return true;
}

public boolean useCustomlookupLedgerDimension(int _accountTypeEnumValue)
{
    boolean returnValue;

    LedgerJournalACType accountType = any2Enum(_accountTypeEnumValue);
    switch (accountType)
    {
        case LedgerJournalACType::Bank:
        case LedgerJournalACType::Cust:
        case LedgerJournalACType::FixedAssets:
        case LedgerJournalACType::Project:
        case LedgerJournalACType::Vend:
        case LedgerJournalACType::RDeferrals:
        case LedgerJournalACType::FixedAssets_RU:
        case LedgerJournalACType::RCash:
        case LedgerJournalACType::Employee_RU:
            returnValue = true;
            break;
        default:
            returnValue = false;
            break;
    }

    return returnValue;
}

/// <summary>
///     Updates query criteria for <c>RPayTrans</c>.
/// </summary>
/// <returns>
///     Number of Employees in criteria.
/// </returns>
public int updateCriteria()
{
    str                 emplIds;
    RHRMEmplTable       localEmplTable;
    RHRMPayMainEmplId   mainEmplId;
    int                 ret = 0;

    criteriaOpen.value(queryValue(endmth(periodDateControl.dateValue())));
    criteriaEmplId.value("");

    if (byWorker && byWorker.value())
    {
        mainEmplId = RHRMEmplTable::find(currentEmplId).PayMainEmplId;

        while select EmployeeId from localEmplTable
            where localEmplTable.PayMainEmplId == mainEmplId
        {
            emplIds += localEmplTable.EmployeeId + ", ";
            ret++;
        }
    }
    else
    {
        emplIds = currentEmplId;
        ret = 1;
    }

    criteriaEmplId.value(emplIds);

    if (sourceTableId && sourceRecId)
    {
        criteriaOpen.value(SysQuery::valueUnlimited());
        criteriaSourceRecId.value(queryValue(sourceRecId));
        criteriaSourceTableId.value(queryValue(sourceTableId));
    }

    return ret;
}

public void updateDesign(boolean _isManyWorkers)
{
    if (currentEmplId)
    {
        byWorker.visible(_isManyWorkers);
        emplId.visible(byWorker.visible() && byWorker.value());

        updateDesign = false;
    }

    if (viewMode)
    {
        byWorker.visible(false);
        emplId.visible(false);
    }
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.enableButtonsActive();
    }

    payTrans_DS.object(fieldNum(RPayTrans, CounterUnit)).allowEdit(payTrans.TaxIncomeGroupCode != '');

    return ret;
}

void initValue()
{
    payTrans.TransDate = endmth(periodDateControl.dateValue());

    if (currentEmplId)
    {
        payTrans.initFromEmplTable(RHRMEmplTable::find(currentEmplId));
    }

    super();
}

public void executeQuery()
{
    RHRMEmplTable       localEmplTable;
    RHRMPayMainEmplId   rhrmPayMainEmplId;

    if (!viewMode)
    {
       element.updateCriteria();
    }

    super();

    if (updateDesign)
    {
        rhrmPayMainEmplId = RHRMEmplTable::find(currentEmplId).PayMainEmplId;
        select count(RecId) from localEmplTable
            where localEmplTable.PayMainEmplId == rhrmPayMainEmplId;
        element.updateDesign(localEmplTable.RecId > 1);
    }

    updateDesign = true;
}

void init()
{
    super();

    this.queryBuildDataSource().clearDynalinks();
    this.cacheAddMethod(tableMethodStr(RPayTrans, costTypeName));

    criteriaOpen   = this.query().dataSourceNo(1).addRange(fieldNum(RPayTrans,PayPeriod));
    criteriaEmplId = this.query().dataSourceNo(1).addRange(fieldNum(RPayTrans, Emplid));
    criteriaSourceTableId = this.query().dataSourceNo(1).addRange(fieldNum(RPayTrans, SourceTableId));
    criteriaSourceRecId = this.query().dataSourceNo(1).addRange(fieldNum(RPayTrans, SourceRecId));

    updateDesign = true;
}

public void linkActive()
{
    ReportPeriod_RU payPeriod;

    element.initEmplTable();

    payPeriod = RPayParameters::payPeriod(currentEmplId);

    periodDateControl.dateValue(payPeriod != endmth(dateNull()) ? payPeriod : dateNull());

    super();
}

display Amount sumNach(RPayTrans _payTransds)
{
    return RPayTrans::getSumAmount(_payTransds.PayPeriod, _payTransds.Emplid, byWorker.value(), RPayChargeType::AddSum);
}

display Amount sumOnHand(RPayTrans _payTransds)
{
    return this.sumNach(_payTransds) - this.sumUder(_payTransds);
}

display Amount sumUder(RPayTrans _payTransds)
{
    return -RPayTrans::getSumAmount(_payTransds.PayPeriod, _payTransds.Emplid, byWorker.value(), RPayChargeType::Deduction);
}

