    {
        payTrans.initFromEmplTable(RHRMEmplTable::find(payTrans.Emplid));
    }
}

public boolean validate()
{
    boolean             ret;

    ret = super();

    if (ret && currentEmplId)
    {
        if (byWorker.value() ?
             RHRMEmplTable::find(currentEmplId).PayMainEmplId != RHRMEmplTable::find(payTrans.Emplid).PayMainEmplId :
             currentEmplId != payTrans.Emplid
          )
        {
            ret = checkFailed("@GLS106221");
        }
    }
    return ret;
}

