void clicked()
{
    RHRMEmplTable               emplTableLocal;
    RPayModuleCalculate         payModuleCalculate;
    RHRMPayMainEmplId           rhrmPayMainEmplId;

    if ( byWorker.value())
    {
        rhrmPayMainEmplId = RHRMEmplTable::find(currentEmplId).PayMainEmplId;

        while select EmployeeId from emplTableLocal
            where emplTableLocal.PayMainEmplId == rhrmPayMainEmplId
        {
            payModuleCalculate = new RPayModuleCalculate(emplTableLocal.EmployeeId);
            payModuleCalculate.calculateAllModule();
        }
    }
    else
    {
        payModuleCalculate = new RPayModuleCalculate(currentEmplId ? currentEmplId : payTrans.Emplid);
        payModuleCalculate.calculateAllModule();
    }

    payTrans_Ds.research();
    super();
}

