private Query createQueryForOtherMembers()
{
    Query                   query;
    QueryBuildDataSource    qDS_PayFundDeductionTable, qDS_PayFundDeductionMember;

    query = new Query();

    qDS_PayFundDeductionTable = query.addDataSource(tablenum(RpayFundDeductionTable));
    qDS_PayFundDeductionTable.addSelectionField(fieldnum(RpayFundDeductionTable, RpayFundDeductionCode));
    qDS_PayFundDeductionTable.addSelectionField(fieldnum(RpayFundDeductionTable, Name));
    qDS_PayFundDeductionTable.addRange(fieldnum(RpayFundDeductionTable, RpayFundDeductionType));
    qDS_PayFundDeductionMember = qDS_PayFundDeductionTable.addDataSource(tablenum(RPayFundDeductionMember));
    qDS_PayFundDeductionMember.addRange(fieldnum(RPayFundDeductionMember, RPayCounterUnit));
    qDS_PayFundDeductionMember.joinMode(JoinMode::NoExistsJoin);
    qDS_PayFundDeductionMember.addLink(
        fieldnum(RpayFundDeductionTable,RpayFundDeductionCode),
        fieldnum(RPayFundDeductionMember,RpayFundDeductionCode));

    return query;
}

private Query createQueryForSelectMembers()
{
    Query                   query;
    QueryBuildDataSource    qDS_PayFundDeductionMember, qDS_PayFundDeductionTable;

    query = new Query();

    qDS_PayFundDeductionMember = query.addDataSource(tablenum(RPayFundDeductionMember));
    qDS_PayFundDeductionMember.addSelectionField(fieldnum(RPayFundDeductionMember, RpayFundDeductionCode));
    qDS_PayFundDeductionMember.addRange(fieldnum(RPayFundDeductionMember, RPayCounterUnit));
    qDS_PayFundDeductionTable = qDS_PayFundDeductionMember.addDataSource(tablenum(RpayFundDeductionTable));
    qDS_PayFundDeductionTable.addSelectionField(fieldnum(RpayFundDeductionTable, Name));

    qDS_PayFundDeductionTable.joinMode(JoinMode::InnerJoin);
    qDS_PayFundDeductionTable.addLink(
        fieldnum(RPayFundDeductionMember, RpayFundDeductionCode),
        fieldnum(RpayFundDeductionTable, RpayFundDeductionCode));

    return query;
}

