public void initMembersLists()
{
    this.initListOtherMembers();
    this.initListSelectMembers();
}

public boolean lookupLedgerDimension(LedgerJournalACType _acType, FormControl _control)
{
    switch (_acType)
    {
        case LedgerJournalACType::Bank:
            BankAccountTable::lookupBankAccount(_control);
            break;
        case LedgerJournalACType::Cust:
            CustTable::lookupCustomer(_control);
            break;
        case LedgerJournalACType::FixedAssets:
            AssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::Project:
            ProjTable::lookupProjId(_control, PayCounterTable);
            break;
        case LedgerJournalACType::Vend:
            VendTable::lookupVendor(_control);
            break;
        case LedgerJournalACType::RDeferrals:
            RDeferralsTable::lookupDeferrals(_control, dateMax());
            break;
        case LedgerJournalACType::FixedAssets_RU:
            RAssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::RCash:
            RCashTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::Employee_RU:
            EmployeeTable_RU::lookupAdvHolder(_control);
            break;
        default:
            return false;
    }
    return true;
}

public boolean useCustomlookupLedgerDimension(int _accountTypeEnumValue)
{
    boolean returnValue;

    LedgerJournalACType accountType = any2Enum(_accountTypeEnumValue);
    switch (accountType)
    {
        case LedgerJournalACType::Bank:
        case LedgerJournalACType::Cust:
        case LedgerJournalACType::FixedAssets:
        case LedgerJournalACType::Project:
        case LedgerJournalACType::Vend:
        case LedgerJournalACType::RDeferrals:
        case LedgerJournalACType::FixedAssets_RU:
        case LedgerJournalACType::RCash:
        case LedgerJournalACType::Employee_RU:
            returnValue = true;
            break;
        default:
            returnValue = false;
            break;
    }

    return returnValue;
}

void membersInOtherList(FormListNext formListNext)
{
    FormListItem formListItem;
    int iIndx;
    RpayFundDeductionCode      fundDeductionCode;

    try
    {
        ttsbegin;

        iIndx = listSelectMembers.getNextItem(formListNext);

        while (iIndx != -1)
        {
            formListItem      = listSelectMembers.getItem(iIndx);
            fundDeductionCode = formListItem.data();
            RPayFundDeductionMember::deleteMember(payCounterTable.CounterUnit, fundDeductionCode);
            iIndx             = listSelectMembers.getNextItem(formListNext, iIndx);
        }

        ttscommit;

        element.initListOtherMembers();
        element.initListSelectMembers();
    }
    catch(Exception::Error)
    {
        ttsabort;
    }
    catch(Exception::Warning)
    {
        ttsabort;
    }
}

