void setLookupButton()
{
    RPayFundCostAccountTable_CostMainAccount.enabled(true);

    if (RPayFundCostAccountTable.AccountCode == TableGroupAll::All)
    {
        RPayFundCostAccountTable_CostMainAccount.enabled(false);
        RPayFundCostAccountTable.CostMainAccount = 0;
    }
}

private void setWhereForListOtherMembers(RPayCounterUnit counterUnit)
{
    QueryBuildDataSource    qDS;
    QueryBuildRange         qRange;

    qDS    = m_qListOtherMembers.dataSourceTable(tablenum(RPayFundDeductionMember));
    qRange = qDS.rangeField(fieldnum(RPayFundDeductionMember, RPayCounterUnit));
    qRange.value(counterUnit);

    qDS    = m_qListOtherMembers.dataSourceTable(tablenum(RpayFundDeductionTable));
    qRange = qDS.rangeField(fieldnum(RpayFundDeductionTable, RpayFundDeductionType));
}

private void setWhereForListSelectMembers(RPayCounterUnit counterUnit)
{
    QueryBuildDataSource    qDS;
    QueryBuildRange         qRange;

    qDS    = m_qListSelectMembers.dataSourceTable(tablenum(RPayFundDeductionMember));
    qRange = qDS.rangeField(fieldnum(RPayFundDeductionMember,RPayCounterUnit));
    qRange.value(counterUnit);
}

void executeQuery()
{
    this.query().dataSourceNo(1).addRange(fieldnum(RPayCounterTable,CounterType)).value(queryValue(RPayCounterType::Fund));
    this.query().dataSourceNo(1).addRange(fieldnum(RPayCounterTable,LineType)).value(queryValue(RPayCtypeType::BudgetFund));
    super();
}

