public Common lookupReference()
{
    Set set = new Set(Types::Enum);

    if (RPayFundCostAccountTable.AccountCode == TableGroupAll::Table)
    {
        set.add(DimensionLedgerAccountType::Expense);
    }
    else
    {
        set.add(DimensionLedgerAccountType::Total);
    }

    return MainAccount::lookupMainAccountByTypes(this, set);
}

