    [Form]
    public class RPayTaxSum extends FormRun
    {
        FormIntControl    taxPeriodControl;
        FormIntControl    deductionCodeControl;
        FormRealControl   deductionSumControl;
        QueryBuildRange   criteriaOpen, correctedPeriod;
        RpayTaxSumCalc    rpayTaxSumCalc;
        [FormObservable]
        RPayAmount        totalDeductionAssessedAmount;
        boolean           oneEmployee;
        ReportPeriod_RU   payPeriodCurrent;

        [FormObservable]
        container         taxSum;
