void calcTax(RHRMPayMainEmplId emplId = '')
{
    RHRMEmplTable   emplTableLocal;
    ReportPeriod_RU currentPeriod,dateStart,dateEnd;

    if (RPayTaxParameters::find().CalcByBranch)
    {
        while select PayMainEmplId from emplTableLocal
            where  emplTableLocal.PayMainEmplId == emplTableLocal.EmployeeId
               && (emplTableLocal.PayMainEmplId == emplId || !emplId)
        {
            RPayTaxCalc::calcTaxForPerson(emplTableLocal.PayMainEmplId);
        }
    }
    else
    {
        dateStart = mkDate(31,1,taxPeriodControl.value());
        dateEnd   = RPayParameters::payPeriod();

        for (currentPeriod = dateStart; currentPeriod <= dateEnd;currentPeriod = endmth(nextMth(currentPeriod)))
        {
            rpayTaxSumCalc.runTax(currentPeriod,emplId);
        }
    }

    rpayTaxEmplSum_Ds.research();
    rpayTaxSumYear_Ds.research();
}

public void clearTotals()
{
    taxSum = [0,0,0,0,0,0];
    totalDeductionAssessedAmount = 0;
}

edit NoYes closedYear(boolean _set, NoYes _close)
{
    TransDate yearPeriod = mkDate(31, 12, taxPeriodControl.value());

    if (_set && (oneEmployee || year(RPayParameters::payPeriod()) >  year(yearPeriod)))
    {
        RPayTaxSumYear::updateClosedState(yearPeriod, _close, oneEmployee ? emplTable.PayMainEmplId : '');
    }

    return RPayTaxSumYear::yrClosedState(yearPeriod, oneEmployee ? emplTable.PayMainEmplId : '');
}

void init()
{
    Common common = element.args().record();
    payPeriodCurrent = RPayParameters::payPeriod();

    if (common.TableId == tableNum(RHRMEmplTable))
    {
        oneEmployee = true;
    }

    super();
    taxPeriodControl = taxPeriod;
    taxPeriodControl.value(year(payPeriodCurrent));
    deductionCodeControl = rpayTaxEmplTrans_RpayTaxDeductionCode;
    deductionSumControl = rpayTaxEmplTrans_Deduction;
    rpayTaxSumCalc = new RpayTaxSumCalc();

    if (oneEmployee)
    {
        all.enabled(false);
        empl.visible(false);
    }
    taxSum = [0,0,0,0,0,0];
}

void setEnableDisableEmplTrans()
{
    RPayTaxIncomeTable rPayTaxIncomeTable;

    if (rpayTaxEmplTrans.RpayTaxType == RpayTaxType::Deduction)
    {
        if (rpayTaxEmplTrans.RecId)
        {
            rpayTaxEmplTrans.RpayTaxDeductionCode = 0;
            rpayTaxEmplTrans.Deduction = 0;
        }
        deductionCodeControl.allowEdit(false);
        deductionSumControl.allowEdit(false);
    }
    else
    {
        rPayTaxIncomeTable = RPayTaxIncomeTable::find(rpayTaxEmplTrans.RpayTaxIncDedCode);
        if (rPayTaxIncomeTable.RpayTaxDeductionCode)
        {
            deductionCodeControl.allowEdit(true);
            deductionSumControl.allowEdit(true);
        }
        else
        {
            if (rpayTaxEmplTrans.RecId)
            {
                rpayTaxEmplTrans.RpayTaxDeductionCode = 0;
                rpayTaxEmplTrans.Deduction = 0;
            }
            deductionCodeControl.allowEdit(false);
            deductionSumControl.allowEdit(false);
        }
    }
    if (rpayTaxEmplTrans.RecId)
    {
        rpayTaxEmplTrans_Ds.reread();
        rpayTaxEmplTrans_Ds.refresh();
    }
}

