void updateTaxSum()
{
    RPayTaxEmplSum   rpayTaxEmplSumLocal;
    RecId            currentSumRecId;

    currentSumRecId = rpayTaxEmplSum.RecId;

    if (rpayTaxEmplSum.EmplId)
    {
        if (rpayTaxEmplSum.Source == NoYes::Yes)
        {
            rpayTaxSumCalc.updateTaxSumOther(rpayTaxEmplSum.PayPeriod,rpayTaxEmplSum.EmplId);
        }

        if (!RPayTaxParameters::find().CalcByBranch)
        {
            rpayTaxSumCalc.updateTaxPeriod(rpayTaxEmplSum.PayPeriod,rpayTaxEmplSum.EmplId);
        }
    }

    rpayTaxEmplsum_Ds.research();
    rpayTaxEmplTrans_Ds.research();
    rPayTaxSumYear_ds.research();
    if (currentSumRecId)
    {
       select firstonly rpayTaxEmplSumLocal where rpayTaxEmplSumLocal.RecId == currentSumRecId;
       rpayTaxEmplSum_ds.positionToRecord(rpayTaxEmplSumLocal);
    }
}

void updateTotals()
{
    RPayTaxReportValue rPayTaxReportValue;

    if (calcPeriodType.selection() == RPayCalcPeriodType::Pay)
    {
        taxSum = RPayTaxEmplSum::getTaxSum(rPayTaxEmplSum.EmplId,
                                           rPayTaxEmplSum.RpayTaxIncomeGroupCode,
                                           dateStartYr(rPayTaxEmplSum.PayPeriod),
                                           rPayTaxEmplSum.PayPeriod);

        rPayTaxReportValue = new RPayTaxReportValue(rPayTaxEmplSum.EmplId, rPayTaxEmplSum.PayPeriod);
    }
    else
    {
        taxSum = RPayTaxEmplSum::getTaxSum(rPayTaxEmplSum.EmplId,
                                           rPayTaxEmplSum.RpayTaxIncomeGroupCode,
                                           dateNull(),
                                           dateMax(),
                                           dateStartYr(rPayTaxEmplSum.CorrectedPeriod),
                                           rPayTaxEmplSum.CorrectedPeriod);

        rPayTaxReportValue = new RPayTaxReportValue(rPayTaxEmplSum.EmplId, dateMax(), 0, false, rPayTaxEmplSum.CorrectedPeriod, rPayTaxEmplSum.CorrectedPeriod);
    }

    totalDeductionAssessedAmount = rPayTaxReportValue.getAssessedDeductionAmount(rPayTaxEmplSum.RpayTaxIncomeGroupCode);
}

