void executeQuery()
{
    if (oneEmployee)
    {
        findOrCreateRange_W(this.query().dataSourceNo(1), fieldNum(RHRMEmplTable, PayMainEmplId)).value('(EmplTable.PayMainEmplId = EmplTable.PayMainEmplId)');
    }
    else
    {
        findOrCreateRange_W(this.query().dataSourceNo(1), fieldNum(RHRMEmplTable, PayMainEmplId)).value('(EmplTable.PayMainEmplId = EmplTable.EmployeeId)');
    }

    super();
}

public void initTaxRateCode()
{
    RPayEmplTax emplTax;

    if (rPayTaxEmplSum.EmplId &&
        rPayTaxEmplSum.RpayTaxIncomeGroupCode &&
        rPayTaxEmplSum.CorrectedPeriod)
    {
        emplTax = RPayEmplTax::construct(
            rPayTaxEmplSum.EmplId,
            rPayTaxEmplSum.CorrectedPeriod,
            dateStartMth(rPayTaxEmplSum.CorrectedPeriod),
            false);
        emplTax.taxStatus(rPayTaxEmplSum.taxStatus());

        rPayTaxEmplSum.TaxRateCode = emplTax.taxGroup2Rate(RPayTaxIncomeGroupTable::findCode(rPayTaxEmplSum.RpayTaxIncomeGroupCode));
    }
}

public boolean validateWrite()
{
    boolean calcByBranch = RPayTaxParameters::find().CalcByBranch;
    boolean ret;

    ret = super();

    if (ret)
    {
        if (calcByBranch && (RPayParameters::payPeriod() != rPayTaxEmplSum.PayPeriod))
        {
            ret = checkFailed("@RUP434");
        }
    }

    return ret;
}

public boolean validateDelete()
{
    boolean calcByBranch = RPayTaxParameters::find().CalcByBranch;
    boolean ret;

    ret = super();

    ret = ret && (!calcByBranch || (RPayParameters::payPeriod() == rPayTaxEmplSum.PayPeriod && calcByBranch));

    return ret;
}

