void executeQuery()
{
    Range dateRange;

    dateRange = SysQuery::range(mkDate(1, 1, taxPeriodControl.value()), mkDate(31, 12, taxPeriodControl.value()));

    if (calcPeriodType.selection() == RPayCalcPeriodType::Pay)
    {
        criteriaOpen.value(dateRange);
        correctedPeriod.value(SysQuery::valueUnlimited());
    }
    else
    {
        correctedPeriod.value(dateRange);
        criteriaOpen.value(SysQuery::valueUnlimited());
    }

    super();
}

