public boolean validate()
{
    boolean ret;

    ret = super();
    if (ret && rpayTaxEmplTrans.RpayTaxDeductionCode)
    {
        if ((select rPayTaxIncomeTable
             where rPayTaxIncomeTable.RpayTaxIncomeCode == rpayTaxEmplTrans.RpayTaxIncDedCode).RpayTaxDeductionCode != rpayTaxEmplTrans.RpayTaxDeductionCode)
        {
            info(strFmt("@GLS105974",rpayTaxEmplTrans.RpayTaxDeductionCode,rpayTaxEmplTrans.RpayTaxIncDedCode));
            ret = false;
        }
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();
    if (ret && rpayTaxEmplTrans.RpayTaxType == RpayTaxType::Income)
    {
        if ((select firstonly rPayTaxIncomeTable where rPayTaxIncomeTable.RpayTaxIncomeCode == rpayTaxEmplTrans.RpayTaxIncDedCode).RecId == 0)
        {
            info(strFmt("@GLS105974",rpayTaxEmplTrans.RpayTaxIncDedCode,rpayTaxEmplSum.RpayTaxIncomeGroupCode));
            ret = false;
        }
    }

    return ret;
}

