public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource  queryBuildDataSource;
    SysTableLookup sysTableLookup;

    if (rpayTaxEmplTrans.RpayTaxType == RpayTaxType::Deduction)
    {
        super();
    }
    else
    {
         sysTableLookup = SysTableLookup::newParameters(tableNum(RPayTaxIncomeTable),this);
         sysTableLookup.addLookupfield(fieldNum(RPayTaxIncomeTable,RpayTaxIncomeCode));
         sysTableLookup.addLookupfield(fieldNum(RPayTaxIncomeTable,Name));
         queryBuildDataSource = query.addDataSource(tableNum(RPayTaxIncomeTable));
         sysTableLookup.parmQuery(query);
         sysTableLookup.performFormLookup();
    }
}

public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource  queryDeductionDataSource,queryIncomeDataSource;
    QueryBuildRange       queryBuildRange;
    SysTableLookup sysTableLookup;

    sysTableLookup = SysTableLookup::newParameters(tableNum(RPayTaxDeductionTable),this);
    sysTableLookup.addLookupfield(fieldNum(RPayTaxDeductionTable,RpayTaxDeductionCode));
    sysTableLookup.addLookupfield(fieldNum(RPayTaxDeductionTable,Name));
    queryDeductionDataSource = query.addDataSource(tableNum(RPayTaxDeductionTable));
    queryIncomeDataSource = queryDeductionDataSource.addDataSource(tableNum(RPayTaxIncomeTable));
    queryBuildRange = queryIncomeDataSource.addRange(fieldNum(RPayTaxIncomeTable,RpayTaxIncomeCode));
    queryBuildRange.value(queryValue(rpayTaxEmplTrans.RpayTaxIncDedCode));
    queryIncomeDataSource.joinMode(JoinMode::ExistsJoin);
    queryIncomeDataSource.addLink(fieldNum(RPayTaxDeductionTable,RpayTaxDeductionCode), fieldNum(RPayTaxIncomeTable,RpayTaxDeductionCode));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

