boolean validate()
{
    boolean ret;
    recId   recId;

    ret = super();

    if (ret)
    {
        if (! (select RPayTblTimeTable where RPayTblTimeTable.PayFormType    == rPayTblJournal.PayFormType &&
                                            rpayTblTimeTable.PayTblTimeCode == this.text()).RecId)
        {
            Box::warning("@RUP6315", "@SYS716");
            ret = false;
        }
        if (ret)
        {
            recId = payTblDayHourTrans.RecId;
            if ((select firstonly RPayTblDayHourTrans index TimeCodeIdx where payTblDayHourTrans.TblTransRecId == payTblTrans.RecId &&
                                                                             payTblDayHourTrans.PayTblTimeCode == this.text() &&
                                                                             payTblDayHourTrans.RecId != recId ).RecId)
            {
                 Box::warning("@RUP6316", "@SYS716");
                 ret = false;
            }
        }
    }

    return ret;
}
