Method.Source:
    [Form]
    public class RPayTblDayHourTrans extends FormRun
    {
        RPayTblJournal              payTblJournal;
        RPayTblTransFormCreate      RPayTblTransFormCreate;
        QueryBuildRange             queryBuildRange;
}

void createLines()
{
    RPayTblTransFormCreate = new RPayTblTransFormCreate(payTblJournal);
    RPayTblTransFormCreate.dialogCaption("@RUP1074");
    RPayTblTransFormCreate.addFields(true);
    if (RPayTblTransFormCreate.prompt())
    {
        if (RPayTblTransFormCreate.checkJournal())
        {
            RPayTblTransFormCreate.createJournalTrans();
            payTblTrans_ds.research();
        }
    }
}

void deleteLines()
{
    ;
    RPayTblTransFormCreate = new RPayTblTransFormCreate(payTblJournal);
    RPayTblTransFormCreate.dialogCaption("@GLS106107");
    RPayTblTransFormCreate.addFields(false);
    if (RPayTblTransFormCreate.prompt())
    {
        if (RPayTblTransFormCreate.checkJournal())
        {
            RPayTblTransFormCreate.deleteJournalTrans();
            payTblTrans_ds.research();
            this.initTmpSumTable();
        }
    }
}

void init()
{
    Common   common = element.args().record();
    boolean  allowModify;

    if (common.TableId == tablenum(RPayTblJournal))
    {
        super();
        payTblJournal = common;
        RPayTblTransFormCreate = new RPayTblTransFormCreate(payTblJournal);
    }
    else
    {
        throw error("@GLS106133");
    }

    allowModify = (endmth(payTblJournal.TblDate) >= RPayParameters::payPeriod());
    payTblTrans_ds.allowCreate(allowModify);
    payTblTrans_ds.allowDelete(allowModify);
    payTblTrans_ds.allowEdit(allowModify);
    payTblDayHourTrans_ds.allowCreate(allowModify);
    payTblDayHourTrans_ds.allowDelete(allowModify);
    payTblDayHourTrans_ds.allowEdit(allowModify);
    createLines.enabled(allowModify);
    deleteLines.enabled(allowModify);
}

void initTmpSumTable()
{
    TmpRPayTimeGroupSum  tmpRPayTimeGroupSum, tmpRpayTimeGroupSumLocal;

    tmpRpayGroupSum.setTmpData(tmpRpayTimeGroupSumLocal);
    tmpRPayTimeGroupSum = RPayTblTransFormCreate.initTmpSumTable(payTblTrans);
    tmpRpayTimesInGroup.setTmpData(tmpRPayTimeGroupSum);

    select sum(Hour),sum(Day) from tmpRPayTimeGroupSum group by TimeGroup;

    while (tmpRPayTimeGroupSum)
    {
        tmpRpayGroupSum.data(tmpRPayTimeGroupSum);
        tmpRpayGroupSum.insert();
        next tmpRPayTimeGroupSum;
    }
    tmpRpayGroupSum_ds.research();
}

void redrawPayTblDayHourTrans()
{
    FormIntControl   control;
    RPayCalendarDate payCalendarDate;
    ;

    while select  TransDate,PayDayType from  payCalendarDate index hint CalendarDateIdx
            where payCalendarDate.CalendarId == payTblTrans.PayCalendarId           &&
                  payCalendarDate.TransDate  >= dateStartMth(payTblTrans.SourceDate) &&
                  payCalendarDate.TransDate  <= endmth(payTblTrans.SourceDate)
    {
        control = element.design().controlName('PayTblDayHourTrans_Day'+int2str(dayofmth(payCalendarDate.TransDate)));
        control.allowEdit(true);
        if (payCalendarDate.PayDayType == RPayDayType::Holiday)
            control.backgroundColor(WindowsPalette::InactiveBorder);
        else
            control.backgroundColor(WindowsPalette::WindowBackground);
    }
}

public void init()
{
    super();

    this.cacheAddMethod(tablemethodstr(RPayTblTrans, payEmplName));
}

int active()
{
    int                  ret;

    ret = super();
    if (ret)
    {
        element.initTmpSumTable();
        element.redrawPayTblDayHourTrans();
        RPayCalendarTimeTrans_DS.queryBuildDataSource().clearRange(fieldNum(RPayCalendarTimeTrans, TblTransRecId));
        RPayCalendarTimeTrans_DS.queryBuildDataSource().addRange(fieldNum(RPayCalendarTimeTrans, TblTransRecId)).value(int642str(PayTblTrans.RecId));
    }

    return ret;
}

public void write()
{
    if (payTblTrans.CorrectedPeriod)
    {
        payTblTrans.CorrectedPeriod = endmth( payTblTrans.CorrectedPeriod);
    }
    super();
}

