public void delete()
{
    super();
    RPayTblEmplSum::calculateEmpl(PayTblTrans.EmplId, PayTblTrans.rpayTblJournal().TblDate , PayTblTrans.PayCalendarId);
    PayTblTrans_ds.active();
}

public void write()
{
    super();
    RPayTblEmplSum::calculateEmpl(PayTblTrans.EmplId, PayTblTrans.rpayTblJournal().TblDate , PayTblTrans.PayCalendarId);
    element.initTmpSumTable();
    RPayCalendarTimeTrans_ds.linkActive();
}

public boolean validateWrite()
{
    boolean             ret = true;
    RPayTblDayHourTrans rPayTblDayHourTransLocal;

    ttsbegin;
    rPayTblDayHourTransLocal =  RPayTblDayHourTrans::find(payTblTrans.RecId,payTblDayHourTrans.PayTblTimeCode,true);

    if (rPayTblDayHourTransLocal.RecId &&
        payTblDayHourTrans.RecId != rPayTblDayHourTransLocal.RecId)
    {
        rPayTblDayHourTransLocal.Visible = NoYes::Yes;
        rPayTblDayHourTransLocal.doUpdate();
        payTblDayHourTrans_DS.research();
        payTblDayHourTrans_DS.positionToRecord(rPayTblDayHourTransLocal);
        ret = false;
    }
    ttscommit;

    ret = ret && super();

    return ret;
}

