Method.Source:
    [Form]
    public class RPayTblTrans extends FormRun
    {
        RPayTblJournal           payTblJournal;
        RPayTblTransFormCreate  RPayTblTransFormCreate;
}

void createLines()
{
    ;
    RPayTblTransFormCreate = new RPayTblTransFormCreate(payTblJournal);
    RPayTblTransFormCreate.dialogCaption("@GLS106106");
    RPayTblTransFormCreate.addFields(true);
    if (RPayTblTransFormCreate.prompt())
    {
        if (RPayTblTransFormCreate.checkJournal())
        {
            RPayTblTransFormCreate.createJournalTrans();
            payTblTrans_ds.research();
        }
    }
}

void deleteLines()
{
    ;
    RPayTblTransFormCreate = new RPayTblTransFormCreate(payTblJournal);
    RPayTblTransFormCreate.dialogCaption("@GLS106107");
    RPayTblTransFormCreate.addFields(false);
    if (RPayTblTransFormCreate.prompt())
    {
        if (RPayTblTransFormCreate.checkJournal())
        {
            RPayTblTransFormCreate.deleteJournalTrans();
            payTblTrans_ds.research();
            this.initTmpSumTable();
        }
    }
}

void init()
{
    Common   common = element.args().record();
    boolean  allowModify;

    if (common.TableId == tablenum(RPayTblJournal))
    {
       super();
       payTblJournal = common;
       RPayTblTransFormCreate = new RPayTblTransFormCreate(payTblJournal);
    }
    else
    {
        throw error("@GLS106133");
    }

    allowModify = (endmth(payTblJournal.TblDate) >= RPayParameters::payPeriod());
    payTblTrans_ds.allowCreate(allowModify);
    payTblTrans_ds.allowDelete(allowModify);
    payTblTrans_ds.allowEdit(allowModify);
    payTrans_ds.allowCreate(allowModify);
    payTrans_ds.allowDelete(allowModify);
    payTrans_ds.allowEdit(allowModify);
    createLines.enabled(allowModify);
    deleteLines.enabled(allowModify);

    if (payTblJournal.PayTransVisible == NoYes::No)
    {
        ctrlSplitVertical.visible(false);
        groupPayTrans.visible(false);
        groupTblTrans.heightMode(1);
        this.arrange();
    }
    else
    {
        groupPayTrans.visible(true);
    }
}

void initTmpSumTable()
{
    TmpRPayTimeGroupSum  tmpRPayTimeGroupSum, tmpRpayTimeGroupSumLocal;

    tmpRpayGroupSum.setTmpData(tmpRpayTimeGroupSumLocal);
    tmpRPayTimeGroupSum = RPayTblTransFormCreate.initTmpSumTable(payTblTrans);
    tmpRpayTimesInGroup.setTmpData(tmpRPayTimeGroupSum);

    select sum(Hour),sum(Day) from tmpRPayTimeGroupSum group by TimeGroup;

    while (tmpRPayTimeGroupSum)
    {
        tmpRpayGroupSum.data(tmpRPayTimeGroupSum);
        tmpRpayGroupSum.insert();
        next tmpRPayTimeGroupSum;
    }
    tmpRpayGroupSum_ds.research();
}

public boolean lookupLedgerDimension(LedgerJournalACType _acType, FormControl _control)
{
    switch (_acType)
    {
        case LedgerJournalACType::Bank:
            BankAccountTable::lookupBankAccount(_control);
            break;
        case LedgerJournalACType::Cust:
            CustTable::lookupCustomer(_control);
            break;
        case LedgerJournalACType::FixedAssets:
            AssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::Project:
            ProjTable::lookupProjId(_control, PayTrans);
            break;
        case LedgerJournalACType::Vend:
            VendTable::lookupVendor(_control);
            break;
        case LedgerJournalACType::FixedAssets_RU:
            RAssetTable::lookupAccountNum(_control);
            break;
        case LedgerJournalACType::RDeferrals:
            RDeferralsTable::lookupDeferrals(_control, dateMax());
            break;
        default:
            return false;
    }
    return true;
}

public boolean useCustomlookupLedgerDimension(int _accountTypeEnumValue)
{
    boolean returnValue;

    LedgerJournalACType accountType = any2Enum(_accountTypeEnumValue);
    switch (accountType)
    {
        case LedgerJournalACType::Bank:
        case LedgerJournalACType::Cust:
        case LedgerJournalACType::FixedAssets:
        case LedgerJournalACType::Project:
        case LedgerJournalACType::Vend:
        case LedgerJournalACType::RDeferrals:
            returnValue = true;
            break;
        default:
            returnValue = false;
            break;
    }

    return returnValue;
}

