}

public void setFieldEnabled()
{
    boolean     isDayEnable;
    boolean     isTimeEnable;
    RPayTblTimeTable    RPayTblTimeTable;

    select firstFast PayTblTimeTypeCalc from RPayTblTimeTable
        where RPayTblTimeTable.PayTblTimeCode == RPayCalendarTimeTrans.PayCalendarTimeCode
           && RPayTblTimeTable.PayFormType ==payTblJournal.PayFormType;

    isTimeEnable = RPayTblTimeTable.PayTblTimeTypeCalc == RPayTblTimeTypeCalc::Hours
                || RPayTblTimeTable.PayTblTimeTypeCalc == RPayTblTimeTypeCalc::Both;

    isDayEnable  = RPayTblTimeTable.PayTblTimeTypeCalc == RPayTblTimeTypeCalc::Days
                || RPayTblTimeTable.PayTblTimeTypeCalc == RPayTblTimeTypeCalc::Both;

    RPayCalendarTimeTrans_Time.allowEdit(isTimeEnable);
    RPayCalendarTimeTrans_Day.allowEdit(isDayEnable);
}

public int active()
{
    int         ret;

    ret = super();

    this.setFieldEnabled();

    return ret;
}

public display RPayTblTimeCodeName timeName(RPayCalendarTimeTrans _timeTrans)
{
    if ( !_timeTrans.PayCalendarTimeCode)
    {
        return "";
    }
    return RPayCalendarTimeTable::find(_timeTrans.PayCalendarTimeCode).ShortName;
}

