boolean checkCompensationTime()
{
    RPayVacationDetermineTime payVacationDetermineTime;
    RHRMEmplTable             emplTable = RHRMEmplTable::find(rPayAverageEarnEmplPeriodTable.EmplId);
    Days                      compensatedDays;
    boolean                   ret = true;

    if (ret && (rPayAverageEarnEmplPeriodTable.StartDate < emplTable.PayEmploymentDate ||
                rPayAverageEarnEmplPeriodTable.StartDate > (emplTable.PayResignedDate ? emplTable.PayResignedDate : maxDate())))
    {
        ret = checkFailed(strFmt("@RUP1890", emplTable.EmployeeId, emplTable.name()));
    }

    if (ret && rPayAverageEarnEmplPeriodTable.EmplId && rPayAverageEarnEmplPeriodTable.StartDate && rPayAverageEarnEmplPeriodTable.VacationTypeId)
    {
        rPayAverageEarnEngine_Vacation.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
        ret = rPayAverageEarnEngine_Vacation.check();

        if (ret)
        {
            payVacationDetermineTime = new RPayVacationDetermineTime(rPayAverageEarnEmplPeriodTable.EmplId,
                                                                     emplTable.PayEmploymentDate,
                                                                     rPayAverageEarnEmplPeriodTable.StartDate,
                                                                     rPayAverageEarnEmplPeriodTable.VacationTypeId);

            compensatedDays = payVacationDetermineTime.getCompensatedDays();

            if (compensatedDays)
            {
                rPayAverageEarnEmplPeriodTable.DayNumber = compensatedDays;
                rPayAverageEarnEmplPeriodTable.EndDate   = dateNull();
            }
            else
            {
                info(strFmt("@RUP2442",
                            emplTable.EmployeeId,
                            emplTable.name(),
                            rPayAverageEarnEmplPeriodTable.VacationTypeId,
                            emplTable.PayEmploymentDate,
                            rPayAverageEarnEmplPeriodTable.StartDate));
            }
        }
    }

    return ret;
}

