public void delete()
{
    RPayAverageEarnEmplPeriodTable rPayAverageEarnEmplPeriodTableOrig;
    boolean                        isReverseLine = rPayAverageEarnEmplPeriodTable.isReverseLine();

    rPayAverageEarnEmplPeriodTableOrig = rPayAverageEarnEmplPeriodTable.data();

    super();

    if (element.formController().needTSUpdate() && rPayAverageEarnEmplPeriodTableOrig.RecId)
    {
        rPayAverageEarnEmplPeriodTableOrig.RecId = 0;
        element.updateTimesheet(rPayAverageEarnEmplPeriodTableOrig, rPayAverageEarnEmplPeriodTableOrig.StartDate, rPayAverageEarnEmplPeriodTableOrig.EndDate);
    }
}

public boolean validateWrite()
{
    RPayAverageEarnEmplPeriodTable origTable =  rPayAverageEarnEmplPeriodTable.orig();
    boolean              ret = true;

    if ( !rPayAverageEarnEmplPeriodTable.Included
      && origTable.SumOnHand == rPayAverageEarnEmplPeriodTable.SumOnHand)
    {
        rPayAverageEarnEngine_Vacation.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
        ret = rPayAverageEarnEngine_Vacation.check();

        if (ret && rPayAverageEarnEmplPeriodTable.VacationRecordType == RPayVacationRecordType::Vacation)
        {
            ret = RpayTblSpecTimeTrans::checkEndDate(RpayTblSpecTimeType::Vacation,
                                                     rPayAverageEarnEmplPeriodTable.EmplId,
                                                     rPayAverageEarnEmplPeriodTable.StartDate,
                                                     rPayAverageEarnEmplPeriodTable.EndDate);
        }
    }

    if (ret)
    {
        ret = super();
    }

    return ret;
}

public void executeQuery()
{
    this.query().dataSourceTable(tableNum(RPayAverageEarnEmplPeriodTable)).
        addRange(fieldNum(RPayAverageEarnEmplPeriodTable, AverageEarnCodeType)).value(queryValue(RPayAverageEarnCodeType::Vacation));

    if (element.args().dataset() == tableNum(RPayAverageEarnEmplPeriodTable))
    {
        this.query().dataSourceTable(tableNum(RPayAverageEarnEmplPeriodTable)).
        addRange(fieldNum(RPayAverageEarnEmplPeriodTable, RecId)).value(con2Str(TransLink_RU::rpayGetReverseInCon(element.args().record())));
    }

    super();
}

public void write()
{
    RPayAverageEarnEmplPeriodTable origTable =  rPayAverageEarnEmplPeriodTable.orig();

    super();

    if ( !rPayAverageEarnEmplPeriodTable.Included
       && origTable.SumOnHand == rPayAverageEarnEmplPeriodTable.SumOnHand)
    {
        if (rPayAverageEarnEngine_Vacation.formController().needTSUpdate() == UnknownNoYes::Yes &&
            (
            origTable.StartDate      != rPayAverageEarnEmplPeriodTable.StartDate ||
            origTable.EndDate        != rPayAverageEarnEmplPeriodTable.EndDate   ||
            origTable.VacationTypeId != rPayAverageEarnEmplPeriodTable.VacationTypeId))
        {
            if (origTable.RecId)
            {
                element.updateTimesheet(origTable,
                                        min(origTable.StartDate, rPayAverageEarnEmplPeriodTable.StartDate),
                                        max(origTable.EndDate, rPayAverageEarnEmplPeriodTable.EndDate));
            }
            else
            {
                element.updateTimesheet(rPayAverageEarnEmplPeriodTable, rPayAverageEarnEmplPeriodTable.StartDate, rPayAverageEarnEmplPeriodTable.EndDate);
            }
        }
        if ( !bufCmp(rPayAverageEarnEmplPeriodTable, origTable))
        {
            rPayAverageEarnEngine_Vacation.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
            rPayAverageEarnEngine_Vacation.calculateNow();
        }
    }
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        rPayAverageEarnEngine_Vacation.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
        element.updateDesign();
    }

    return ret;
}

edit RPayVacationDuration rpayWeekDuration(boolean _set,
                                           RPayAverageEarnEmplPeriodTable _rPayAverageEarnEmplPeriodTable,
                                           RPayVacationDuration           _vacationDuration)
{
    RPayWeekDuration rPayWeekDuration = _rPayAverageEarnEmplPeriodTable.rPayWeekDuration;
    if (_set)
    {
        if (_vacationDuration == RPayVacationDuration::CalendarDays)
        {
            _rPayAverageEarnEmplPeriodTable.rPayWeekDuration = RPayWeekDuration::SevenDays;
        }
        else
        {
            _rPayAverageEarnEmplPeriodTable.rPayWeekDuration = RPayWeekDuration::SixDays;
        }
        if (rPayAverageEarnEmplPeriodTable.RecId)
        {
            rPayAverageEarnEngine_Vacation.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
            if (! rPayAverageEarnEngine_Vacation.check())
            {
                _rPayAverageEarnEmplPeriodTable.rPayWeekDuration = rPayWeekDuration;
            }
        }

        // Recalculate day number
        rPayAverageEarnEmplPeriodTable.DayNumber = 0;
        element.updateEndDate();
    }

    if (_rPayAverageEarnEmplPeriodTable.rPayWeekDuration == RPayWeekDuration::SevenDays)
    {
        return RPayVacationDuration::CalendarDays;
    }
    else
    {
        return RPayVacationDuration::WorkDays;
    }
}

