public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup       sysTableLookup = SysTableLookup::newParameters(tableNum(RPayAverageEarnEmplPeriodTable),_formControl);
    Query                query = new Query();
    QueryBuildDataSource qBd;

    if (rPayAverageEarnEmplPeriodTable.EmplId)
    {
        qBd = query.addDataSource(tableNum(RPayAverageEarnEmplPeriodTable));

        sysTableLookup.addLookupfield(fieldNum(RPayAverageEarnEmplPeriodTable, StartDate));
        sysTableLookup.addLookupfield(fieldNum(RPayAverageEarnEmplPeriodTable, EndDate));
        sysTableLookup.addLookupfield(fieldNum(RPayAverageEarnEmplPeriodTable, VacationTypeId));

        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, rPayAverageEarnCode)).value(rPayAverageEarnEmplPeriodTable.rPayAverageEarnCode);
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, EmplId)).value(rPayAverageEarnEmplPeriodTable.EmplId);
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, VacationTypeId)).value(rPayAverageEarnEmplPeriodTable.VacationTypeId);
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, Included)).value(queryValue(NoYes::Yes));
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, VacationRecordType)).value(queryValue(RPayVacationRecordType::Vacation));
        qBd.addRange(fieldNum(RPayAverageEarnEmplPeriodTable, RecId)).value(SysQuery::valueNot(rPayAverageEarnEmplPeriodTable.RecId));

        sysTableLookup.parmQuery(query);
        sysTableLookup.performFormLookup();
    }
}

public boolean validate()
{
    RPayAverageEarnEmplPeriodTable prevVacation;
    boolean ret;

    ret = super();
    if (ret && rPayAverageEarnEmplPeriodTable.PrevVacationStartDate)
    {
        prevVacation = rPayAverageEarnEmplPeriodTable.previousVacation();
        if (!prevVacation || prevVacation.RecId == rPayAverageEarnEmplPeriodTable.RecId)
        {
            ret = checkFailed(strFmt("@RUP2443", rPayAverageEarnEmplPeriodTable.PrevVacationStartDate, rPayAverageEarnEmplPeriodTable.EmplId));
        }
        else
        {
            rPayAverageEarnEngine_Vacation.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
            rPayAverageEarnEngine_Vacation.initFromPreviousVacation();
        }
    }

    if (ret)
    {
        element.updateDesign();
    }
    return ret;
}

