#define.MainTabSheetNum(1)
public boolean tabChange(int _fromTab)
        {
            boolean ret = true;

            if (_fromTab == #MainTabSheetNum)
            {
                //enforce writing to prevent active() calling
                ret = rpayAverageEarnEmplPeriodTable_ds.validateWrite() && rPayAverageEarnEngine_Vacation.check();
                if (ret)
                {
                    rpayAverageEarnEmplPeriodTable_ds.write();
                }
            }

            return super(_fromTab);
        }

public boolean modified()
{
    boolean ret;

    ret = super();
    if (ret)
    {
        if (rpayAverageEarnEmplPeriodTable_ds.validateWrite())
        {
            rpayAverageEarnEmplPeriodTable_ds.write();
        }
    }
    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (rpayAverageEarnEmplPeriodTable_ds.validateWrite())
        {
            rpayAverageEarnEmplPeriodTable_ds.write();
        }
    }
    return ret;
}

void clicked()
{
    if (! rPayAverageEarnEmplPeriodTable.Included)
    {
        rPayAverageEarnEngine_Vacation.rPayAverageEarnEmplPeriodTable(rPayAverageEarnEmplPeriodTable);
        rPayAverageEarnEngine_Vacation.calculateNow();
        rPayAverageEarnEmplPeriodTable_ds.refresh();
        rPayAverageEarnEmplPeriodTable_ds.reread();
        rPayAverageEarnTrans_ds.research();
    }
    else
    {
        info (strFmt("@GLS106123","@GLS106125"));
    }
}
