[Form]
public class RTax25DebtAmortisation extends FormRun
{
    [FormObservable]
    RTax25DebtReserve               reserveAmountMST;
    [FormObservable]
    AmountMST                       markupAmountMST;
    [FormObservable]
    AmountMST                       markupExpenseOrRevenue;
    [FormObservable]
    AmountMST                       markupReserve;
    [FormObservable]
    AmountMST                       fromReserve;
    [FormObservable]
    AmountMST                       fromExpenseOrRevenue;
    [FormObservable]
    AmountMST                       totalAmountMST;

    RTax25DebtReverse               debtProcessClass;
    boolean                         isReverse;
    [FormObservable]
    TransDate                       transDate, dateFrom, dateTo;

    RTax25DebtType                  debtType;

    QueryBuildRange                 custDateRange, vendDateRange;

    RTax25DebtAmortisation          debtReverse;

    str                             caption;

    Common                          common;

    Query                           qCustTransOpen;
    Query                           qVendTransOpen;

    private const str AboveZero = '>0';
    private const str BelowZero = '<0';

}

void calcAmounts(boolean _dateChanged = false)
{
    RTax25DebtModel             rTax25DebtModel;
    RTax25DebtAmounts           debtAmounts;
    RTax25RegisterJournalTable  registerJournalTable;
    FormDesign                  design = element.design();

    markupAmountMST         = 0;
    markupExpenseOrRevenue  = 0;
    markupReserve           = 0;
    element.updateControls();

    reserveAmountMST        = 0;
    fromReserve             = 0;
    totalAmountMST          = 0;
    fromExpenseOrRevenue    = 0;

    debtProcessClass.clearMarkups();

    try
    {
        rTax25DebtModel = RTax25DebtModel::findRecId(debtModel.selection() == RTax25DebtModelType::RAP ?
                                                 CustParameters::rTax25DebtModel()                 :
                                                 CustParameters::rTax25DebtModelTax());

        debtAmounts = RTax25DebtAmounts::newModelParameters(
            transDate,
            debtType,
            NoYes::Yes,
            rTax25DebtModel.AccountingPolicy == RTax25DebtPolicy::Document,
            rTax25DebtModel.AccountingPolicy == RTax25DebtPolicy::Document,
            debtModel.selection(),
            NoYes::Yes);

        reserveAmountMST     = debtAmounts.getReserve();
        fromReserve          = debtAmounts.getUsedReserve();
        totalAmountMST       = debtAmounts.getWrittenOffAmount();

        if (totalAmountMST)
        {
            fromExpenseOrRevenue = totalAmountMST - fromReserve;
        }

        registerJournalTable = debtAmounts.getRegisterJour();

        if (_dateChanged)
        {
            debtProcessClass.clearMarkups();

            if (isReverse)
            {
                dateFrom = debtAmounts.getFromDate();
                dateTo   = debtAmounts.getToDate();
            }
            else
            {
                dateFrom = dateNull();
                dateTo   = transDate  - debtAmounts.hopelessDebtOffset();
            }

            design.caption(strFmt("@GLS111169",
                                  caption,
                                  "@SYS6790",
                                  registerJournalTable.JournalNum,
                                  registerJournalTable.Name));
        }

        custDateRange.value(queryRange(dateFrom,dateTo));
        custTrans_ds.executeQuery();

        vendDateRange.value(queryRange(dateFrom,dateTo));
        vendTrans_ds.executeQuery();
    }
    catch(Exception::Error)
    {
        design.caption(caption);

        custDateRange.value(SysQuery::value(dateNull()));
        custTrans_ds.executeQuery();

        vendDateRange.value(SysQuery::value(dateNull()));
        vendTrans_ds.executeQuery();
    }
}

display AmountMST fromExpenseOrRevenue()
