public void init()
{
    transDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    if ( ! this.args() || this.args().parmEnumType() != enumNum(RTax25DebtType))
        throw error("@SYS22996");

    debtType = this.args().parmEnum();

    const str MnuTextParmReverse = 'Reverse';

    if (this.args().parm() == MnuTextParmReverse)
    {
        debtProcessClass = RTax25DebtReverse::construct();
        isReverse        = true;
    }
    else
    {
        debtProcessClass = RTax25DebtAmortisation::construct(debtType);
        isReverse        = false;
    }

    common = this.args().record();

    super();

    calcDateCtrl.dateValue(transDate);

    if (debtType == RTax25DebtType::Debit)
    {
        reserveAmountMSTCtrl.visible(true);
        markupAmountMSTCtrl.visible(true);

        fromReserveCtrl.visible(true);
        fromExpenseOrRevenueCtrl.visible(true);
        debtModel.visible(true);

        if (isReverse)
        {
            markupReserveCtrl.visible(true);
            markupExpenseOrRevenueCtrl.visible(true);

            btnCustTransOffset.visible(true);
            btnVendTransOffset.visible(true);

            cust_fromReserve.visible(true);
            vend_fromReserve.visible(true);
            cust_fromExpenseOrRevenue.visible(true);
            vend_fromExpenseOrRevenue.visible(true);
            cust_amountMST.visible(true);
            vend_amountMST.visible(true);
            caption = "@GLS104356";
        }
        else
        {
            cust_IsError.visible(true);
            vend_IsError.visible(true);
            cust_dueDate.visible(true);
            vend_dueDate.visible(true);
            custTrans_Invoice.visible(true);
            vendTrans_Invoice.visible(true);
            cust_remainCur.visible(true);
            vend_remainCur.visible(true);
            cust_remainMST.visible(true);
            cust_remainMST.visible(true);
            custTrans_CurrencyCode.visible(true);
            vendTrans_CurrencyCode.visible(true);
            selectCust.visible(true);
            selectVend.visible(true);
            resetSelection.visible(true);

            caption = "@GLS103828";
        }
    }
    else
    {
        if (isReverse)
        {
            caption = "@GLS104358";
            cust_amountMST.visible(true);
            vend_amountMST.visible(true);

            btnCustTransOffset.visible(true);
            btnVendTransOffset.visible(true);
        }
        else
        {
            cust_IsError.visible(true);
            vend_IsError.visible(true);
            cust_dueDate.visible(true);
            vend_dueDate.visible(true);
            custTrans_Invoice.visible(true);
            vendTrans_Invoice.visible(true);
            cust_remainCur.visible(true);
            vend_remainCur.visible(true);
            cust_remainMST.visible(true);
            vend_remainMST.visible(true);
            custTrans_CurrencyCode.visible(true);
            vendTrans_CurrencyCode.visible(true);

            caption = "@GLS104194";
        }
    }

    if (common.RecId)
    {
        switch (common.TableId)
        {
            case tableNum(CustTrans) :
                vendGroup.visible(false);
                transDate = common.(fieldNum(CustTrans,TransDate));
            break;
            case tableNum(VendTrans) :
                custGroup.visible(false);
                transDate = common.(fieldNum(VendTrans,TransDate));
            break;
        }
    }
    else
    {
    }

    this.calcAmounts(true);
}

