/// <summary>
/// Initializes temporary data source for filtering.
/// </summary>
/// <param name = "_custVend">The parameter detemining customer or vendor module.</param>
public void initTmpFilter(ModuleCustVend _custVend)
{
    QueryRun        queryRun;
    CustTransOpen   custTransOpen;
    VendTransOpen   vendTransOpen;

    ttsBegin;

    switch(_custVend)
    {
        case ModuleCustVend::Cust :
            delete_from tmpRecIdFilterCust;

            queryRun = new queryRun(qCustTransOpen);
            while (queryRun.next())
            {
                custTransOpen = queryRun.get(tableNum(CustTransOpen));

                tmpRecIdFilterCust.RefRecId = custTransOpen.RefRecId;
                tmpRecIdFilterCust.insert();
            }
            break;
        case ModuleCustVend::Vend :
            delete_from tmpRecIdFilterVend;

            queryRun = new queryRun(qVendTransOpen);
            while (queryRun.next())
            {
                vendTransOpen = queryRun.get(tableNum(VendTransOpen));

                tmpRecIdFilterVend.RefRecId = vendTransOpen.RefRecId;
                tmpRecIdFilterVend.insert();
            }
            break;
    }

    ttsCommit;
}

/// <summary>
/// Performs transaction selection.
/// </summary>
/// <param name = "_custVend">The parameter detemining customer or vendor module.</param>
public void selectFromTrans(ModuleCustVend _custVend)
{
    const str ShowOpenControlName = 'ShowOpen';
    Query                   q = new Query();
    QueryRun                qr;
    QueryBuildDataSource    qbds;
    Args                    args;
    FormRun                 formRun;
    CustVendTrans           custVendTrans;
    FormCheckBoxControl     ctrlShowOpen;
    MultiSelectionHelper    helper = MultiSelectionHelper::construct();

    switch (_custVend)
    {
        case ModuleCustVend::Cust :
            qbds = q.addDataSource(tableNum(CustTrans));
            findOrCreateRange_W(qbds, fieldNum(CustTrans, TransDate), queryRange(dateNull(), transDate)).status(RangeStatus::Locked);
            findOrCreateRange_W(qbds, fieldNum(CustTrans, AmountMST), '>0').status(RangeStatus::Locked);
            args = new args(formStr(CustTrans));
            break;
        case ModuleCustVend::Vend :
            qbds = q.addDataSource(tableNum(VendTrans));
            findOrCreateRange_W(qbds, fieldNum(VendTrans, TransDate), queryRange(dateNull(), transDate)).status(RangeStatus::Locked);
            findOrCreateRange_W(qbds, fieldNum(VendTrans, AmountMST), '>0').status(RangeStatus::Locked);
            args = new args(formStr(VendTrans));
            break;
    }

    qr = new QueryRun(q);
    if (qr.prompt())
    {
        args.parmObject(qr.query());

        formRun = classFactory.formRunClass(args);
        formRun.init();

        ctrlShowOpen = formRun.design().controlName(ShowOpenControlName);
        ctrlShowOpen.value(NoYes::Yes);

        formRun.run();

        helper.parmDatasource(formRun.dataSource());
        formRun.wait();

        custVendTrans = helper.getFirst();
        while (custVendTrans)
        {
            switch (_custVend)
            {
                case ModuleCustVend::Cust :
                    tmpRecIdFilterCust.RefRecId = custVendTrans.RecId;
                    tmpRecIdFilterCust.insert();
                    break;
                case ModuleCustVend::Vend :
                    tmpRecIdFilterVend.RefRecId = custVendTrans.RecId;
                    tmpRecIdFilterVend.insert();
                    break;
            }
            custVendTrans = helper.getNext();
        }

        if (_custVend == ModuleCustVend::Cust)
        {
            custTrans_ds.executeQuery(false);
        }
        else
        {
            vendTrans_ds.executeQuery(false);
        }
    }
}

display DueDate dueDate(CustTrans _custTrans)
{
    return CustTransOpen::findRefId(_custTrans.RecId).DueDate;
}

public int active()
{
    int ret;

    ret = super();

    if (isReverse)
        return ret;

    if ( ! custTrans.Invoice)
    {
        if ( ! custTrans.Prepayment)
        {
            cust_MarkTrans.allowEdit(false);
            return ret;
        }
    }

    cust_MarkTrans.allowEdit(true);

    return ret;
}

display AmountMST amountMST(CustTrans _custTrans)
{
    return abs(_custTrans.AmountMST);
}

display AmountMST fromExpenseOrRevenue(CustTrans _custTrans)
{
    return abs(_custTrans.AmountMST) - abs(debtModel.selection() == RTax25DebtModelType::TAX ?
        _custTrans.custTrans_W().rTax25Amortisation_RU    :
        _custTrans.custTrans_W().RAPAmortisation_RU);
}

display AmountMST fromReserve(CustTrans _custTrans)
{
    return abs(debtModel.selection() == RTax25DebtModelType::TAX ?
        _custTrans.custTrans_W().rTax25Amortisation_RU    :
        _custTrans.custTrans_W().RAPAmortisation_RU);
}

edit NoYes markTrans(boolean            _set,
                     CustTrans          _custTrans,
                     NoYes              _markTrans)
{
    NoYes   ret = _markTrans;
    CustTrans_W custTrans_W;
    AmountMST   reserveWrittenOff;

    if (_set)
    {
        custTrans_W = _custTrans.custTrans_W();
        reserveWrittenOff = debtModel.selection() == RTax25DebtModelType::TAX   ?
                            custTrans_W.rTax25Amortisation_RU                   :
                            custTrans_W.RAPAmortisation_RU;

        if (_markTrans == NoYes::Yes)
        {
            if (debtProcessClass.appendMarkup(_custTrans, transDate))
            {
                if (isReverse)
                {
                    markupAmountMST += abs(_custTrans.AmountMST);
                    markupReserve   += abs(reserveWrittenOff);
                    markupExpenseOrRevenue += abs(_custTrans.AmountMST) - abs(reserveWrittenOff);
                }
                else
                {
                    markupAmountMST += abs(CurrencyExchangeHelper::amountCur2MST(
                        _custTrans.remainAmountCur(),
                        _custTrans.CurrencyCode,
                        0,
                        transDate));
                }

                element.updateControls();
            }
            else
            {
                ret = NoYes::No;
                error("@SYS29065");
            }
        }
        else
        {
            debtProcessClass.removeMarkup(_custTrans);

            if (isReverse)
            {
                markupAmountMST -= abs(_custTrans.AmountMST);
                markupReserve   -= abs(reserveWrittenOff);
                markupExpenseOrRevenue -= abs(_custTrans.AmountMST) - abs(reserveWrittenOff);
            }
            else
            {
                markupAmountMST -= abs(CurrencyExchangeHelper::amountCur2MST(
                    _custTrans.remainAmountCur(),
                    _custTrans.CurrencyCode,
                    0,
                    transDate));
            }

            element.updateControls();
        }
    }
    else
    {
        ret = debtProcessClass.existsMarkup(_custTrans);
    }
    return ret;
}

display CustName custName(CustTrans _custTrans)
{
    return CustTable::find(_custTrans.AccountNum).name();
}

public void init()
{
    QueryBuildDataSource    qbds, settleTrans, transOffset, openTrans;
    QueryBuildRange         qbr, qbr2;

    super();

    qbds  = this.query().dataSourceTable(tableNum(CustTrans));

    if (common.RecId)
    {
        qbds.addRange(fieldNum(CustTrans, RecId)).value(SysQuery::value(common.RecId));
    }

    if (isReverse)
    {
        tmpRecIdFilterCust_ds.linkType(FormLinkType::Passive);

        qbds.orderMode(OrderMode::OrderBy);
        qbds.addSortField(fieldNum(CustTrans, TransDate));
        qbds.addSortField(fieldNum(CustTrans, AccountNum));

        custDateRange = qbds.addRange(fieldNum(CustTrans, TransDate));
        custDateRange.value(SysQuery::range(dateFrom, dateTo));

        settleTrans = qbds.addDataSource(tableNum(CustSettlement)/*, "CustSettlement"*/);
        settleTrans.fetchMode(0);
        settleTrans.relations(true);
        settleTrans.joinMode(JoinMode::NoExistsJoin);

        transOffset = settleTrans.addDataSource(tableNum(CustTrans)/*, "CustTransOffset"*/);
        transOffset.fetchMode(0);
        transOffset.relations(false);
        transOffset.joinMode(JoinMode::InnerJoin);

        transOffset.addLink(fieldNum(CustSettlement, OffsetRecid), fieldNum(CustTrans, RecId));

        qbr   = qbds.addRange(fieldNum(CustTrans, TransType));
        qbr2  = transOffset.addRange(fieldNum(CustTrans, TransType));

        if (debtType == RTax25DebtType::Debit)
        {
            qbr.value(enum2Value(LedgerTransType::RTax25_BadDebtDebitAmortisation));
            qbr2.value(enum2Value(LedgerTransType::RTax25_BadDebtDebitAmortisation));
        }
        else
        {
            qbr.value(enum2Value(LedgerTransType::RTax25_BadDebtCreditAmortisation));
            qbr2.value(enum2Value(LedgerTransType::RTax25_BadDebtCreditAmortisation));
        }
    }
    else
    {
        qCustTransOpen = new Query();
        openTrans = qCustTransOpen.addDataSource(tableNum(CustTransOpen));

        custDateRange = openTrans.addRange(fieldNum(CustTransOpen, DueDate));
        custDateRange.value(SysQuery::range(dateFrom, dateTo));

        qbr = openTrans.addRange(fieldNum(CustTransOpen, AmountCur));

        if (debtType == RTax25DebtType::Debit)
        {
            qbr.value(AboveZero);
        }
        else
        {
            qbr.value(BelowZero);
        }
    }
}

