{
    super();

    element.selectFromTrans(ModuleCustVend::Vend);
}

void clicked()
{
    super();

    markupAmountMST         = 0;
    markupExpenseOrRevenue  = 0;
    markupReserve           = 0;
    element.updateControls();

    debtProcessClass.clearMarkups();
    custTrans_ds.executeQuery();
    vendTrans_ds.executeQuery();
}

void clicked()
{
    super();

    if (debtProcessClass.updateNow())
    {
        element.calcAmounts();
    }
}

void clicked()
{
    CustSettlement settlement;
    CustTrans transOffset;

    select firstonly transOffset
        join RecId from settlement
        where transOffset.RecId     == settlement.OffsetRecid &&
              settlement.TransRecId == custTrans.RecId;

    Args args = new Args();
    args.caller(element);
    args.record(transOffset);

    MenuFunction action = this.menufunction();
    action.run(args);
}

void clicked()
{
    VendSettlement settlement;
    VendTrans transOffset;

    select firstonly transOffset
        join RecId from settlement
        where transOffset.RecId     == settlement.OffsetRecid &&
              settlement.TransRecId == vendTrans.RecId;

    Args args = new Args();
    args.caller(element);
    args.record(transOffset);

    MenuFunction action = this.menufunction();
    action.run(args);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if ( ! this.dateValue())
    {
        this.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    }

    transDate = this.dateValue();

    element.calcAmounts(true);

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.calcAmounts(true);

    return ret;
}
