public class RTax25DebtModel extends FormRun
{
}

public void setLookupButtonCust()
{
    custNum.allowEdit(true);
    custNum.skip(false);
    custNum.lookupButton(0);

    custNumHeader.allowEdit(true);
    custNumHeader.skip(false);
    custNumHeader.lookupButton(0);

    if (rTax25DebtLedgerAccountsCust.AccountCode== TableGroupAll::All)
    {
        custNum.allowEdit(false);
        custNum.skip(true);
        custNum.lookupButton(1);

        custNumHeader.allowEdit(false);
        custNumHeader.skip(true);
        custNumHeader.lookupButton(1);

        rTax25DebtLedgerAccountsCust.Num = '';
    }
}

public void setLookupButtonVend()
{
    vendNum.allowEdit(true);
    vendNum.skip(false);
    vendNum.lookupButton(0);

    vendNumHeader.allowEdit(true);
    vendNumHeader.skip(false);
    vendNumHeader.lookupButton(0);

    if (rTax25DebtLedgerAccountsVend.AccountCode== TableGroupAll::All)
    {
        vendNum.allowEdit(false);
        vendNum.skip(true);
        vendNum.lookupButton(1);

        vendNumHeader.allowEdit(false);
        vendNumHeader.skip(true);
        vendNumHeader.lookupButton(1);

        rTax25DebtLedgerAccountsVend.Num = '';
    }
}

public void updateDesign()
{
    boolean isRAPModel = rTax25DebtModel.RecId == CustParameters::rTax25DebtModel(false);

    ledgerPosting.visible(RTax25DebtModel.AccountingPolicy == RTax25DebtPolicy::Total);
    custLedger.visible(RTax25DebtModel.AccountingPolicy == RTax25DebtPolicy::Document);
    vendLedger.visible(RTax25DebtModel.AccountingPolicy == RTax25DebtPolicy::Document);

    rTax25DebtModel_ds.object(fieldNum(RTax25DebtModel, SummaryLedgerDimension)).enabled(! isRAPModel);
    rTax25DebtLedgerAccountsCust_ds.object(fieldNum(RTax25DebtLedgerAccounts, SummaryLedgerDimension)).visible(! isRAPModel);
    rTax25DebtLedgerAccountsVend_ds.object(fieldNum(RTax25DebtLedgerAccounts, SummaryLedgerDimension)).visible(! isRAPModel);
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign();

    return ret;
}

