Method.Source:
[Form]
public class RTax25RegisterJournalTrans extends FormRun
{
    RTax25RegisterJournalTable rTax25RegisterJournalTable;
    RTax25RegCalcController rTax25RegCalcController;
    FormDataObject status;
    FormDataObject approved;
    FormDataObject approver;

    boolean nextJournalNotExists;
    boolean prevCorrectionProcessed;
    boolean updating;
    boolean assessedTax;
    boolean transportTax;
    boolean landTax;

}

public void init()
{
    Common common = element.args().record();

    if (common.TableId == tableNum(RTax25RegisterJournalTable))
    {
         rTax25RegisterJournalTable = common;
    }
    else
    {
        throw error("@GLS103799");
    }

    assessedTax  = rTax25RegisterJournalTable.isOfTaxType(RAssetTaxType::AssessedTax);
    transportTax = rTax25RegisterJournalTable.isOfTaxType(RAssetTaxType::TransportTax);
    landTax      = rTax25RegisterJournalTable.isOfTaxType(RAssetTaxType::LandTax);

    rTax25RegCalcController = new RTax25RegCalcController();
    rTax25RegCalcController.rTax25RegisterJournalTable(rTax25RegisterJournalTable);

    if (! rTax25RegCalcController.createJournalTrans())
    {
        throw info("@GLS104094");
    }

    super();

    status = rtax25RegisterJournalTrans_ds.object(fieldNum(RTax25RegisterJournalTrans,Status));
    approved = rtax25RegisterJournalTrans_ds.object(fieldNum(RTax25RegisterJournalTrans,Approved));
    approver = rtax25RegisterJournalTrans_ds.object(fieldNum(RTax25RegisterJournalTrans,Approver));
    nextJournalNotExists =  RTax25RegCalcController::nextJournalNotExists(rTax25RegisterJournalTable,false);
    prevCorrectionProcessed = rTax25RegCalcController.prevCorrectionProcessed();
    buttonCalcCurrentRegister.enabled(nextJournalNotExists && prevCorrectionProcessed);
    buttonCalcAllRegisters.enabled(nextJournalNotExists  && prevCorrectionProcessed);
    buttonDelete.enabled(nextJournalNotExists);
    buttonDefaultStatus.enabled(nextJournalNotExists);

    if (rTax25RegisterJournalTable.RegisterJournalType == RTax25RegisterJournalType::Asset_RU)
    {
        rtax25RegisterJournalTrans_ds.object(fieldNum(RTax25RegisterJournalTrans, RegisterId)).visible(false);
    }
    else if (rTax25RegisterJournalTable.RegisterJournalType == RTax25RegisterJournalType::ProfitAllocation)
    {
        RTax25AllocPrepareCalc.visible(true);
        ButtonCalcAllRegisters.visible(false);
        RTax25AllocCalcDetails.visible(true);
    }

    Correction.visible(rTax25RegisterJournalTable.Version > 0 &&
        rTax25RegisterJournalTable.RegisterJournalType == RTax25RegisterJournalType::ProfitTax);
    RTax25RegisterJournalTrans_ds.query().dataSourceTable(tablenum(RTax25RegisterJournalTrans)).addRange(fieldnum(RTax25RegisterJournalTrans, Cancelled)).value(QueryValue(NoYes::No));
}

void updateDesign()
{
    RTax25RegisterJournalTrans  notCalculatedJournalTrans;
    boolean                     existOldData;

    status.allowEdit(! rTax25RegisterJournalTrans.Approved);
    approver.allowEdit(rTax25RegisterJournalTrans.Approved);
    approved.allowEdit(rTax25RegisterJournalTrans.Status == RTax25RegisterStatus::Calculated);

    boolean isTransJournalForTaxDiffRegisterByBalance = (RTax25RegisterTable::findRegisterId(rTax25RegisterJournalTrans.RegisterId).RegisterType == RTax25RegisterType::ConstantDifferencesByBalance
            || RTax25RegisterTable::findRegisterId(rTax25RegisterJournalTrans.RegisterId).RegisterType == RTax25RegisterType::TemporaryDifferencesByBalance)
        && !LedgerJournalCreate_Tax25_RU::notExistReverseJournal(rTax25RegisterJournalTable, LedgerJournalType::RTax25TaxDiffByBalance, false);

    boolean isTransJournalForTaxDiffRegister = (RTax25RegisterTable::findRegisterId(rTax25RegisterJournalTrans.RegisterId).RegisterType == RTax25RegisterType::ConstantDifferences
            || RTax25RegisterTable::findRegisterId(rTax25RegisterJournalTrans.RegisterId).RegisterType == RTax25RegisterType::TemporaryDifferences)
        && !LedgerJournalCreate_Tax25_RU::notExistReverseJournal(rTax25RegisterJournalTable, LedgerJournalType::RTax25, false);

    boolean isTransJournalForDebtDebitReservation = (RTax25RegisterTable::findRegisterId(rTax25RegisterJournalTrans.RegisterId).RegisterType == RTax25RegisterType::DebtDebitReservationDetail
            || RTax25RegisterTable::findRegisterId(rTax25RegisterJournalTrans.RegisterId).RegisterType == RTax25RegisterType::DebtDebitReservationDetailRAP)
        && !LedgerJournalCreate_Tax25_RU::notExistReverseJournal(rTax25RegisterJournalTable, LedgerJournalType::RTax25DebtDebitReserve, false);

    boolean isTransJournalForAmountDifference = RTax25RegisterTable::findRegisterId(rTax25RegisterJournalTrans.RegisterId).RegisterType == RTax25RegisterType::AmountDifference
        && !LedgerJournalCreate_Tax25_RU::notExistReverseJournal(rTax25RegisterJournalTable, LedgerJournalType::RTax25AmountDifference, false);

    boolean isTransJournalForTaxExchangeAdj = RTax25RegisterTable::findRegisterId(rTax25RegisterJournalTrans.RegisterId).RegisterType == RTax25RegisterType::TaxExchangeAdj
        && !LedgerJournalCreate_Tax25_RU::notExistReverseJournal(rTax25RegisterJournalTable, LedgerJournalType::RTax25ExchDifference, false);

    boolean isTransJournalForTaxRemainGoods = RTax25RegisterTable::findRegisterId(rTax25RegisterJournalTrans.RegisterId).RegisterType == RTax25RegisterType::TaxRemainGoods
        && !LedgerJournalCreate_Tax25_RU::notExistReverseJournal(rTax25RegisterJournalTable, LedgerJournalType::RTax25TaxRemainGoods, false);

    boolean isTransJournalForAssessedTax = ((assessedTax && !LedgerJournalCreate_Tax25_RU::notExistReverseJournal(rTax25RegisterJournalTable, LedgerJournalType::RAssetAssessedTax, false))
            || (transportTax && !LedgerJournalCreate_Tax25_RU::notExistReverseJournal(rTax25RegisterJournalTable, LedgerJournalType::RAssetTransportTax, false))
            || (landTax && !LedgerJournalCreate_Tax25_RU::notExistReverseJournal(rTax25RegisterJournalTable, LedgerJournalType::RAssetLandTax, false)))
        && rTax25RegisterJournalTrans.AssessedTaxRegisterType != RAssetAssessedTaxRegType::None;

    if (isTransJournalForTaxDiffRegisterByBalance
        || isTransJournalForTaxDiffRegister
        || isTransJournalForDebtDebitReservation
        || isTransJournalForAmountDifference
        || isTransJournalForTaxExchangeAdj
        || isTransJournalForTaxRemainGoods
        || isTransJournalForAssessedTax)
    {
        approver.allowEdit(false);
        approved.allowEdit(false);
    }

    if (! rTax25RegisterJournalTrans.Approved)
    {
        rTax25RegisterJournalTrans.Approver = 0;
    }
    buttonCalcCurrentRegister.enabled(
        nextJournalNotExists    &&
        prevCorrectionProcessed &&
        rTax25RegisterJournalTrans.Status == RTax25RegisterStatus::NotCalculated);

    select firstonly RecId from notCalculatedJournalTrans
        where notCalculatedJournalTrans.JournalNum      == rTax25RegisterJournalTable.JournalNum    &&
              notCalculatedJournalTrans.FiscalYear      == rTax25RegisterJournalTable.FiscalYear    &&
              notCalculatedJournalTrans.PeriodType      == rTax25RegisterJournalTable.PeriodType    &&
              notCalculatedJournalTrans.PeriodNum       == rTax25RegisterJournalTable.PeriodNum     &&
              notCalculatedJournalTrans.Cancelled       == NoYes::No                                &&
              notCalculatedJournalTrans.Status          == RTax25RegisterStatus::NotCalculated;

    buttonCalcAllRegisters.enabled(
        nextJournalNotExists    &&
        prevCorrectionProcessed &&
        notCalculatedJournalTrans.RecId);

    existOldData = RTax25RegisterTrans::existAny(rTax25RegisterJournalTrans.JournalNum,
                                                 rTax25RegisterJournalTrans.RegisterId,
                                                 rTax25RegisterJournalTrans.PeriodType,
                                                 rTax25RegisterJournalTrans.PeriodNum,
                                                 rTax25RegisterJournalTrans.FiscalYear);

    rTax25RegisterTransOld.visible(existOldData);
    rTax25RegisterTrans.visible(!existOldData);
    rTax25RegisterTransOld.enabled(rTax25RegisterJournalTrans.Status != RTax25RegisterStatus::UnUse);
    rTax25RegisterTrans.enabled(rTax25RegisterJournalTrans.Status != RTax25RegisterStatus::UnUse);
    rTax25RegisterTransReport.enabled(rTax25RegisterJournalTrans.Status == RTax25RegisterStatus::Calculated);

    if (rTax25RegisterJournalTrans.RegisterJournalType == RTax25RegisterJournalType::ProfitAllocation)
    {
        RTax25AllocPrepareCalc.enabled(
            !RTax25AllocCalcDetails::existOnDate(
                rTax25RegisterJournalTrans.toDate()));
    }

    //Allow edit datasource only in case register was created in current journal
    RTax25RegisterJournalTrans_DS.allowEdit(
        nextJournalNotExists &&
        rTax25RegisterJournalTrans.JournalNum == rTax25RegisterJournalTable.JournalNum &&
       (rTax25RegisterJournalTable.Version == 0 ||
        rTax25RegisterJournalTable.Accepted != RTax25CorrectionAccepted::Accepted ||
        !rTax25RegisterJournalTable.approved()));
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();
    if (ret)
    {
        if (rTax25RegisterJournalTrans.Approved && ! rTax25RegisterJournalTrans.Approver)
        {
            ret = checkFailed("@SYS67768");
        }
    }

    return ret;
}

public int active()
{
    int ret;

    ret = super();
    if (ret)
    {
        element.updateDesign();
    }
    return ret;
}

display ImageRes imageIdx(RTax25RegisterJournalTrans _rTax25RegisterJournalTrans)
{
    const int ImageNum1 = 1;
    const int ImageNum2 = 2;
    int ret;

    switch (_rTax25RegisterJournalTrans.Status)
    {
        case RTax25RegisterStatus::NotCalculated:
            ret = 0;
            break;
        case RTax25RegisterStatus::Calculated:
            ret = ImageNum1;
            break;
        case RTax25RegisterStatus::UnUse:
            ret = ImageNum2;
            break;
    }
    return ret;
}

public void init()
{
    super();

    rTax25RegCalcController.modifyFormQuery(this.query());
}

public void modified()
{
    super();

    element.updateDesign();
}

public boolean validate()
{
    boolean ret;
    if (updating)
        return true;

    ret = super();
    if (ret)
    {
        ret =  rTax25RegCalcController.registerStatusValidate(rTax25RegisterJournalTrans);
    }

    return ret;
}

public void modified()
{
    RTax25RegisterJournalTrans registerJournalTrans = rTax25RegisterJournalTrans.data();
    super();
    rTax25RegCalcController.updateParentRegisterStatus(rTax25RegisterJournalTrans);
    updating = true;
    rTax25RegisterJournalTrans_ds.research();
    rTax25RegisterJournalTrans_ds.positionToRecord(registerJournalTrans);
    updating = false;
    element.updateDesign();
}

