    boolean       ret;
    ret = super();

    return ret;
}

void clicked()
{
    RTax25RegisterJournalTrans registerJournalTrans = rTax25RegisterJournalTrans.data();
    rTax25RegCalcController.deleteJournalLines();
    rTax25RegisterJournalTrans_ds.research();
    rTax25RegisterJournalTrans_ds.positionToRecord(registerJournalTrans);
}

void clicked()
{
    RTax25RegCalc rTax25RegCalc = RTax25RegCalc::construct(RTax25RegisterTable::registerId2Type(rTax25RegisterJournalTrans.RegisterId),
                                                           rTax25RegisterJournalTrans.AssessedTaxRegisterType);
    rTax25RegCalc.init(rTax25RegisterJournalTrans);
    if (rTax25RegCalc.prompt())
    {
        rTax25RegCalcController.calcRegister(rTax25RegisterJournalTrans,true);
    }
}

void clicked()
{
    void dummy()
    {
    }
    try
    {
        if (rTax25RegCalcController.prompt())
        {
            rTax25RegCalcController.calcJournal();
        }
    }
    catch (Exception::Error)
    {
        dummy();
    }
    catch (Exception::Info)
    {
        dummy();
    }
    catch (Exception::Warning)
    {
        dummy();
    }
    catch (Exception::Break)
    {
        dummy();
    }
    rTax25RegisterJournalTrans_ds.research();
    element.updateDesign();
}

void clicked()
{
    RTax25RegisterJournalTrans registerJournalTrans = rTax25RegisterJournalTrans.data();
    rTax25RegCalcController.setDefaultStatus();
    rTax25RegisterJournalTrans_ds.research();
    rTax25RegisterJournalTrans_ds.positionToRecord(registerJournalTrans);
}

void clicked()
{
    AddRecalculate.enabled(RTax25RegisterJournalTrans.JournalNum != rTax25RegisterJournalTable.JournalNum);
    RemoveRecalculate.enabled(RTax25RegisterJournalTrans.JournalNum == rTax25RegisterJournalTable.JournalNum);

    super();
}

void clicked()
{
    super();

    rTax25RegCalcController.addRecalculate(
        RTax25RegisterJournalTrans.RegisterId,
        RTax25RegisterJournalTrans,
        true);
}

void clicked()
{
    super();

    rTax25RegCalcController.removeRecalculate(
        RTax25RegisterJournalTrans.RegisterId,
        RTax25RegisterJournalTrans,
        true);
}

