void buildForm()
{
    Name name;

    if (!sysQueryForm.queryValidate())                      // If no datasource exist then close the form.
    {
        okPressed = true;
        element.close();
    }

    if (sysQueryForm.isReport())                            // If this query has a report running.
    {
        if (sysQueryForm.hasGeneratedDesign())               // If the report has a generated design then show
        {
            // only the tree fist columns in the sortingTable.
            sorting_ds.object(fieldNum(TmpSysQuery, AutoSum)).visible(false);
            sorting_ds.object(fieldNum(TmpSysQuery, AutoHeader)).visible(false);
            sortingDetailLevelGrp.visible(false);
            printOptionsTab.visible(false);
        }
        else // SYP-Modification HQ-731-825-ND5C (SP1)
        {
            if (!sysQueryForm.hasReportTemplate())
            {
                printRangeBox.enabled(false);
            }
        }
    }
    else
    {
        sorting_ds.object(fieldNum(TmpSysQuery, AutoSum)).visible(false);
        sorting_ds.object(fieldNum(TmpSysQuery, AutoHeader)).visible(false);
        sortingDetailLevelGrp.visible(false);
        printOptionsTab.visible(false);
    }

    name = sysQueryForm.queryActiveLabel();

    if (sysQueryForm.userUpdate())                          // If the query allows the user to update the query.
    {
        if (sysQueryForm.allowSave())                       // If the queryRun allow the query to be saved in sysLastValue.
        {
            if (!xSysLastValue::getLast(element))
            {
                if (!this.v25RenameSaved())
                {
                    if (sysQueryForm.isForm())
                    {
                        this.v21RenameSaved();
                    }
                }
            }

            if (sysQueryForm.loadLastUsedQuery())
            {
                name = sysQueryForm.queryLastUsedLabel();
            }
        }
        else
        {
            fileBtn.enabled(false);
            fileGrp.allowEdit(false);
        }

        resetButton.visible(sysQueryForm.showReset()) ;
        fileGrp.visible(sysQueryForm.showQuerySelect()) ;

        if (!sysQueryForm.showSorting())
        {
            sortingTab.visible(false);
        }
    }
    else
    {
        fileBtn.enabled(                sysQueryForm.userUpdate());
        resetButton.enabled(            sysQueryForm.userUpdate());

        range_ds.allowDelete(           sysQueryForm.userUpdate());
        range_ds.allowCreate(           sysQueryForm.userUpdate());

        rangeAdd.enabled(               sysQueryForm.userUpdate());
        rangeRemove.enabled(            sysQueryForm.userUpdate());

        sortingTab.allowEdit(           sysQueryForm.userUpdate());
        sortAdd.enabled(                sysQueryForm.userUpdate());
        sortRemove.enabled(             sysQueryForm.userUpdate());
        sortDown.enabled(               sysQueryForm.userUpdate());
        sortUp.enabled(                 sysQueryForm.userUpdate());

        rangeTab.allowEdit(             sysQueryForm.userUpdate());
        sortingDetailLevelGrp.allowEdit(sysQueryForm.userUpdate());
        companyRangesTab.allowEdit(     sysQueryForm.userUpdate());
        printOptionsTab.allowEdit(      sysQueryForm.userUpdate());
    }
    savedQueriesBox.selectText(name);

    this.queryLoad();
}

boolean canClose()
{
    boolean ret = true;
    if (!sysQueryForm.validateCompanyRanges(companyRanges) && this.closedOk())
    {
        Box::info("@SYS113177");
        companyRangesTab.activatePage();
        ret = false;
    }

    if (!sysQueryForm.validateFieldsForCompany(range, companyRanges) && this.closedOk())
    {
        ret = false;
    }

    return ret;
}

void close()
{
    if (sysQueryForm.query().dataSourceCount())
    {
        if (element.okPressed())
        {
            if (sysQueryForm.userUpdate())
            {
                element.updateTable(range, rangeUpdate);
                element.updateTable(sorting, sortingUpdate);
                element.updateTable(companyRanges, companyRangesUpdate);
                sysQueryForm.queryBuild(range, sorting, companyRanges, timeRange);

                if (sysQueryForm.allowSave())
                {
                    element.querySave(sysQueryForm.queryLastUsedLabel(), true);
                }
                sysQueryForm.parmPrintGrandTotal(printGrandTotalBox.value());
                sysQueryForm.parmPrintOnlyTotals(printOnlyTotalsBox.value());
                sysQueryForm.parmPrintRanges(printRangeBox.value());
                sysQueryForm.parmPrintRemoveRepeatedHeaders(printRepeatedHeadersBox.value());
                sysQueryForm.parmPrintRemoveRepeatedFooters(printRepeatedTotalsBox.value());
                sysQueryForm.queryUpdate();
            }
        }
        if (sysQueryForm.allowSave())
        {
            xSysLastValue::saveLast(element);
        }
    }

    super();
}

