void init()
{
    super();

    sysQueryForm = new SysQueryForm(element.args().caller());

    QueryRun qRun = element.args().caller();
    Query q = qRun.query();

    int dsCount = q.dataSourceCount();
    int rangeCount;
    boolean isCompositeQuery;
    QueryBuildDataSource ds;
    QueryBuildRange qrange;

    for (int i = 1; i <= dsCount; i++)
    {
        ds = q.dataSourceNo(i);
        rangeCount = ds.rangeCount();

        for (int j = 1; j <= rangeCount; j++)
        {
            qrange = ds.range(j);

            if (qrange.doesRangeNodeBelongToCompositeQuery())
            {
                isCompositeQuery = true;
                break;
            }
        }
    }

    sysQueryForm.isCompositeQuerySource(isCompositeQuery);
}

