void queryLoad(LabelType _name = savedQueriesBox.getText(savedQueriesBox.selection()))
{
    boolean         allowAddAndDelete;
    sysQueryForm.queryLoad(_name);

    allowAddAndDelete = sysQueryForm.allowAddAndDelete();

    range_ds.allowCreate(allowAddAndDelete);
    range_ds.allowDelete(allowAddAndDelete);

    sorting_ds.allowCreate(allowAddAndDelete);
    sorting_ds.allowDelete(allowAddAndDelete);

    companyRangesTab.visible(sysQueryForm.allowsCompanyRange());

    sysQueryForm.resetSortingIndex();

    sysQueryForm.buildCompanyRanges(companyRanges);
    sysQueryForm.buildRange(range, companyRanges);
    sysQueryForm.buildSorting(sorting);

    sysQueryForm.buildDataSourceTree(dataSourceTree);

    if (printOptionsTab.visible())
    {
        if (printRangeBox.enabled())
        {
            printRangeBox.value(sysQueryForm.parmPrintRanges());
        }
        if (printOnlyTotalsBox.enabled())
        {
            printOnlyTotalsBox.value(sysQueryForm.parmPrintOnlyTotals());
        }
        if (printGrandTotalBox.enabled())
        {
            printGrandTotalBox.value(sysQueryForm.parmPrintGrandTotal());
        }
        if (printRepeatedHeadersBox.enabled())
        {
            printRepeatedHeadersBox.value(sysQueryForm.parmPrintRemoveRepeatedHeaders());
        }
        if (printRepeatedTotalsBox.enabled())
        {
            printRepeatedTotalsBox.value(sysQueryForm.parmPrintRemoveRepeatedFooters());
        }
    }
    element.disableArrangeButtons();
    element.disableRemoveButtons();

    sorting_ds.query().dataSourceNo(1).sortClear();
    sorting_ds.query().dataSourceNo(1).addSortIndex(indexNum(TmpSysQuery, SortNoIdx));

    sorting_ds.executeQuery();
    range_ds.executeQuery();
    companyRanges_ds.executeQuery();

    switch (_name)
    {
        case sysQueryForm.queryLastUsedLabel():
            saveBtn.enabled(false);
            deleteBtn.enabled(true);
            break;
        case sysQueryForm.queryActiveLabel():
            saveBtn.enabled(false);
            deleteBtn.enabled(false);
            break;
        default:
            saveBtn.enabled(true);
            deleteBtn.enabled(true);
            break;
    }
}

public void querySave(LabelType _name = '', boolean _updateDropDownBox = true)
{
    Dialog          saveDialog;
    DialogField     dialogField;
    boolean         validate;
    Name            name;

    if (_name)
    {
        validate = false;
        name     = _name;
    }
    else
    {
        validate   = true;
        saveDialog = new Dialog();
        saveDialog.caption("@SYS26193");
        dialogField = saveDialog.addField(extendedTypeStr(Name));

        if (!saveDialog.run())
        {
            return;
        }
        name = dialogField.value();
    }

    if (name)
    {
        sysQueryForm.parmPrintRanges(printRangeBox.value());
        sysQueryForm.parmPrintGrandTotal(printGrandTotalBox.value());
        sysQueryForm.parmPrintOnlyTotals(printOnlyTotalsBox.value());
        sysQueryForm.parmPrintRemoveRepeatedHeaders(printRepeatedHeadersBox.value());
        sysQueryForm.parmPrintRemoveRepeatedFooters(printRepeatedTotalsBox.value());

        sysQueryForm.queryBuild(range, sorting, companyRanges, timeRange);
        sysQueryForm.querySave(name, validate);

        if (_updateDropDownBox)
        {
            sysQueryForm.buildSavedQueriesBox(savedQueriesBox);
            savedQueriesBox.selectText(name);
            this.queryLoad(name);
        }
    }
}

