private boolean v21RenameSaved()
{
    QueryBuildDataSource    queryBuildDataSource;
    boolean                 ret;
    str                     tableText;
    UtilElementType         newElementType;
    str                     newDesignName;
    str                     newElementName;
    str                     oldDesignName;

    for (int i = 1; i <= sysQueryForm.query().dataSourceCount(); i++)
    {
        queryBuildDataSource    = sysQueryForm.query().dataSourceNo(i);

        if (queryBuildDataSource.uniqueId() < sysQueryForm.queryNextUniqueId())
        {
            if (tableText)
            {
                tableText = tableText + ',';
            }
            tableText = tableText + int2str(queryBuildDataSource.table());
        }
    }

    const str TableType = 'T:';
    oldDesignName = TableType + tableText;

    newElementType = sysQueryForm.lastValueType();
    newElementName = sysQueryForm.lastValueElementName();
    newDesignName  = sysQueryForm.lastValueDesignName();

    sysQueryForm.lastValueType(UtilElementType::Form);
    sysQueryForm.lastValueElementName(this.name());
    sysQueryForm.lastValueDesignName(oldDesignName);

    if (xSysLastValue::existLast(this))
    {
        ret           = true;
        v21PackFormat = true;

        if (xSysLastValue::getLast(this))
        {
            xSysLastValue::deleteLast(this);

            sysQueryForm.lastValueType(newElementType);
            sysQueryForm.lastValueElementName(newElementName);
            sysQueryForm.lastValueDesignName(newDesignName);

            xSysLastValue::saveLast(this);
        }
        v21PackFormat = false;
    }

    sysQueryForm.lastValueType(newElementType);
    sysQueryForm.lastValueElementName(newElementName);
    sysQueryForm.lastValueDesignName(newDesignName);

    return ret;
}

private boolean v25RenameSaved()
{
    boolean ret;
    UtilElementType         newElementType;
    str                     newDesignName;
    str                     newElementName;
    newElementType = sysQueryForm.lastValueType();
    newElementName = sysQueryForm.lastValueElementName();
    newDesignName  = sysQueryForm.lastValueDesignName();

    sysQueryForm.lastValueType(UtilElementType::Form);
    sysQueryForm.lastValueElementName(this.name());
    sysQueryForm.lastValueDesignName(sysQueryForm.getV25SaveName());

    if (xSysLastValue::existLast(this))
    {
        ret             = true;
        v25PackFormat   = true;

        if (xSysLastValue::getLast(this))
        {
            xSysLastValue::deleteLast(this);
            sysQueryForm.lastValueType(newElementType);
            sysQueryForm.lastValueElementName(newElementName);
            sysQueryForm.lastValueDesignName(newDesignName);
            xSysLastValue::saveLast(this);
        }
        v25PackFormat = false;
    }

    sysQueryForm.lastValueType(newElementType);
    sysQueryForm.lastValueElementName(newElementName);
    sysQueryForm.lastValueDesignName(newDesignName);

    return ret;
}

