public boolean validate()
{
    boolean ret;
    Query query;
    QueryBuildDataSource dataSource;
    int last, current;
    int i;

    ret = super();

    last = infologLine();
    query = new Query();
    dataSource = query.addDataSource(range.Table_Id);
    dataSource.addRange(range.Field_Id).value(range.RangeValue);
    current = infologLine();

    if (last != current)
    {
        for (i = last+1; i <= current; i++)
        {
            if (infolog.level(i) == Exception::Error)
            {
                ret = false;
            }
        }
    }
    return ret;
}

