public void write()
{
    TmpSysQuery tmpSysQuery;

    if (!sorting.RecId)
    {
        sorting.SortingNo = sysQueryForm.sortingNoAdd(sorting.DataSourceNo);
    }
    sorting.TableOccurrence = sysQueryForm.sortingMapId(sorting.DataSourceNo);

    super();

    if (sorting.RecId)
    {
        tmpSysQuery.setTmpData(sorting);

        select firstonly tmpSysQuery
            index SortingIdx
            where tmpSysQuery.RecId == sorting.RecId;

        this.executeQuery();
    }
}

