int active()
{
    boolean allowAddAndDelete;
    boolean allowEdit;
    boolean allowChangeOrder;
    boolean isVisible;
    int     ret;

    if (sysQueryForm.userUpdate())
    {
        if (sorting_ds.getPosition() > 0)
        {
            sortRemove.enabled(true);
        }

        if (sorting.RecId)
        {
            allowAddAndDelete = sysQueryForm.allowAddAndDelete(sorting.DataSourceNo) &&
                                (!sysQueryForm.hasGroupByDataSources());
            allowEdit         = sysQueryForm.allowEdit(sorting.DataSourceNo);
            isVisible         = sysQueryForm.fieldIsVisible(sorting.DataSourceNo, sorting.Prefix_Id, companyRanges, sorting.Field_Id, false);

            this.object(fieldNum(TmpSysQuery, FieldLabel)).allowEdit(allowAddAndDelete);
            this.object(fieldNum(TmpSysQuery, Tablelabel)).allowEdit(allowAddAndDelete);
            this.object(fieldNum(TmpSysQuery, SortingDirection)).allowEdit(allowEdit);

            element.allowEditAutoFields(isVisible);

            this.object(fieldNum(TmpSysQuery, QueryAutoHeaderDetailLevel)).allowEdit(sorting.AutoHeader);
            this.object(fieldNum(TmpSysQuery, QueryAutoSumDetailLevel)).allowEdit(sorting.AutoSum);

            allowChangeOrder = true;

            sortUp.enabled(allowChangeOrder);
            sortDown.enabled(allowChangeOrder);

            if (allowChangeOrder)
            {
                if (sorting.SortingNo == 1)
                {
                    sortUp.enabled(false);
                }

                if (sorting.SortingNo == sysQueryForm.getSortingNo(sorting.DataSourceNo))
                {
                    sortDown.enabled(false);
                }
            }
        }
    }
    ret = super();

    return ret;
}

