/// <summary>
/// Edit method for the SelectAll check box on the CompanyRanges tab.
/// </summary>
/// <param name="setData">
/// Whether this is called for setting or getting the data.
/// </param>
/// <param name="checked">
/// For the edit action, whether the box is checked or not.
/// </param>
/// <returns>
/// Whether the box should be checked or not.
/// </returns>
edit boolean selectAllCheckBoxEditMethod(boolean setData, boolean checked)
{
    TmpSysQueryCompanyRange tmp;

    tmp.setTmpData(companyRanges);

    if (setData)
    {
        while select * from tmp
        {
            tmp.IsCompanySelected = checked;
            tmp.write();
        }

        companyRanges.setTmpData(tmp);
        companyRanges_ds.executeQuery();

        return checked;
    }
    else
    {
        while select * from tmp
        {
            if (!tmp.IsCompanySelected)
            {
                return false;
            }
        }

        return true;
    }
}

