[Form]
public class ReqActionGraph extends FormRun
{
    #SysSystemDefinedButtons

    private ReqActionGraphForm      actionGraphForm;
    private RefRecId            	selectedReqTransRecId;

    private boolean             	savedShowOnlyActionable;
    private boolean             	savedShowOnlyRelated;

    private boolean             	keepCurrentCenterNode;

    private ReqActionGraphControl   ctrlActionGraph;

    private const int CurrentVersion = 1;
    #localmacro.CurrentList
        savedShowOnlyActionable,
        savedShowOnlyRelated
    #endmacro

}

public void findTmpRecord(RecId _recId, boolean _keepCurrentCenterNode = true)
{
    keepCurrentCenterNode = _keepCurrentCenterNode;

    // Need to use the findValue here as the positionToRecordByValue do not work with temp tables
    ReqTransTmp_DS.findValue(fieldNum(ReqTrans, RecId), int642str(actionGraphForm.reqTransRecIdToTmpRecId(selectedReqTransRecId)));
}

public void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    xSysLastValue::getLast(element);

    actionGraphForm = ReqActionGraphForm::construct();

    actionGraphForm.parmShowOnlyActionable(savedShowOnlyActionable);
    actionGraphForm.parmShowOnlyRelated(savedShowOnlyRelated);

    super();

    FormControl popoutButton = element.design().controlName(#SystemDefinedPopoutButton);
    if (popoutButton)
    {
        popoutButton.visible(false);
    }

    element.initializeGraphControl();
}

public void close()
{
    super();

    savedShowOnlyActionable = actionGraphForm.parmShowOnlyActionable();
    savedShowOnlyRelated = actionGraphForm.parmShowOnlyRelated();

    xSysLastValue::saveLast(element);
}

public void initParmDefault()
{
    savedShowOnlyActionable = true;
    savedShowOnlyRelated = false;
}

public DataAreaId lastValueDataAreaId()
{
    return curext();
}

public IdentifierName lastValueDesignName()
{
    return '';
}

public IdentifierName lastValueElementName()
{
    return formStr(ReqActionGraph);
}

public UtilElementType lastValueType()
{
    return UtilElementType::Form;
}

public UserId lastValueUserId()
{
    return curUserId();
}

public container pack()
{
    return [CurrentVersion, #CurrentList];
}

public boolean unpack(container packedClass)
{
    int version = RunBase::getVersion(packedClass);

    switch (version)
    {
        case CurrentVersion:
            [version, #CurrentList] = packedClass;
            return true;
    }

    return false;
}

public boolean parmShowOnlyActionable()
{
    return actionGraphForm.parmShowOnlyActionable();
}

public boolean parmShowOnlyRelated()
{
    return actionGraphForm.parmShowOnlyRelated();
}

public void initializeGraphControl()
{
    ctrlActionGraph = graphTabPage.addControlEx(classStr(ReqActionGraphControl), 'ctrlActionGraph');
    ctrlActionGraph.widthMode(FormWidth::SizeToAvailable);
    ctrlActionGraph.heightMode(FormWidth::SizeToAvailable);

    ctrlActionGraph.onSelectedNodeChanged += eventhandler(element.onSelectedNodeChanged);
    ctrlActionGraph.onFocusedNodeChanged += eventhandler(element.onFocusedNodeChanged);
}

private void onSelectedNodeChanged(str _nodeId)
{
    var selectedReqTransRecIdClient = str2int64(_nodeId);

    if (actionGraphForm.canSelectReqTransRecId(selectedReqTransRecIdClient))
    {
        selectedReqTransRecId = selectedReqTransRecIdClient;

        if (actionGraphForm.parmShowOnlyRelated())
    	{
    	    actionGraphForm.constructFilteredActionGraphModel(selectedReqTransRecId);

    	    element.refreshGraph();
    	    ReqTransTmp_DS.executeQuery();
    	}

    	element.findTmpRecord(actionGraphForm.reqTransRecIdToTmpRecId(selectedReqTransRecId), false);
    }
}

private void onFocusedNodeChanged(str _nodeId)
{
    var focusedReqTransRecIdClient = str2int64(_nodeId);

    if (selectedReqTransRecId != focusedReqTransRecIdClient && actionGraphForm.canSelectReqTransRecId(focusedReqTransRecIdClient))
    {
        selectedReqTransRecId = focusedReqTransRecIdClient;

        element.findTmpRecord(actionGraphForm.reqTransRecIdToTmpRecId(selectedReqTransRecId), true);
    }
}

public void refreshGraph()
{
    ctrlActionGraph.actionGraphData(actionGraphForm.getActionGraphData());
}

public void refreshTitle()
{
    element.design().caption(strFmt("@SCM:ReqActionGraphTitle", "@GLS3800124", actionGraphForm.numberOfVisibleActions(), actionGraphForm.numberOfActions()));
}

public void refreshForm()
{
    actionGraphForm.constructFilteredActionGraphModel(selectedReqTransRecId);

    selectedReqTransRecId = actionGraphForm.findVisibleReqTransRecId(selectedReqTransRecId);

    element.refreshGraph();

    ReqTransTmp_DS.executeQuery();

    element.findTmpRecord(actionGraphForm.reqTransRecIdToTmpRecId(selectedReqTransRecId), false);
}

public void init()
{
    super();

    this.queryBuildDataSource().clearDynalinks();
    this.queryBuildDataSource().clearRanges();

    this.queryBuildDataSource().addDynalink(fieldNum(ReqTrans, RecId), element.args().record(), fieldNum(ReqTrans, RecId));
}

public int active()
{
    int ret;

    ret = super();

    actionGraphForm.clear();
    actionGraphForm.initializeBaseModel(reqTransCaller);

    selectedReqTransRecId = reqTransCaller.RecId;

    element.refreshForm();

    return ret;
}

public int active()
{
    int ret;
    RefRecId reqTransRecId;

    ret = super();

    if (!keepCurrentCenterNode)
    {
        reqTransRecId = actionGraphForm.tmpRecIdToReqTransRecId(reqTransTmp.RecId);
        ctrlActionGraph.centeredNode(any2Str(reqTransRecId));
        ctrlActionGraph.focusedNode(any2Str(reqTransRecId));
    }
    else
    {
        keepCurrentCenterNode = false;
    }

    buttonApplySingle.enabled(actionGraphForm.canApplyReqTransTmp(reqTransTmp));

    return ret;
}

public void init()
{
    reqTransTmp.setTmp();

    super();
}

public void executeQuery()
{
    QueryBuildDataSource qbds = this.queryBuildDataSource();

    qbds.clearDynalinks();
    qbds.clearRanges();

    // Populate the reqTrans data source with the nodes
    reqTransTmp.setTmpData(actionGraphForm.getReqTransTmp());

    if (actionGraphForm.parmShowOnlyActionable())
    {
        this.addOnlyActionableReqRefTypesFilter();
    }

    super();

    if (!selectedReqTransRecId)
    {
        selectedReqTransRecId = actionGraphForm.tmpRecIdToReqTransRecId(reqTransTmp.RecId);
    }
    ctrlActionGraph.selectedNode(any2Str(selectedReqTransRecId));

    element.refreshTitle();
}

private void addOnlyActionableReqRefTypesFilter()
{
    QueryBuildDataSource    qbds            = this.queryBuildDataSource();
    DictEnum                reqRefTypeEnum  = DictEnum::newFromName(enumStr(ReqRefType));
    Enumerator              enum            = reqRefTypeEnum.getEnumerator();

    while (enum.moveNext())
    {
        ReqRefType value = enum.current();
        if (ReqTrans::isReqRefTypeActionable(value))
        {
            QueryBuildRange qbr = qbds.addRange(fieldNum(ReqTrans, RefType));
            qbr.value(queryValue(value));
        }
    }
}

void clicked()
{
    super();

    if (actionGraphForm.applyToSingleNode(str2Int64(ctrlActionGraph.focusedNode())))
    {
        actionGraphForm.constructFilteredActionGraphModel(selectedReqTransRecId);
        ReqTransTmp_DS.executeQuery();
        element.refreshGraph();
    }
}

public void dialogClosed(xFormRun _formRun)
{
    super(_formRun);

    if (_formRun.closedOk())
    {
        Object object = _formRun;

        actionGraphForm.parmShowOnlyRelated(object.parmShowOnlyRelated());
        actionGraphForm.parmShowOnlyActionable(object.parmShowOnlyActionable());

        element.refreshForm();
    }
}
