[Form]
public class ReqDemPlanEasyItemAllocator extends FormRun
{
    ForecastItemAllocation  itemAllocation;
    QueryBuildRange         qbdsForecastLineRange;

}

public void init()
{
    if (   !element.args()
        || !element.args().record()
        || element.args().record().TableId != tableNum(ForecastItemAllocation) )
    {
        throw error(error::missingFormActiveBuffer(formStr(ReqDemPlanEasyItemAllocator), tableStr(ForecastItemAllocation)));
    }

    itemAllocation = element.args().record();
    ReqDemPlanBlankDimensions::findOrCreate();
    super();
}

/// <summary>
/// Refreshes the grids by re-executing the datasource queries.
/// </summary>
public void refresh()
{
    ForecastItemAllocationLine_DS.research();
}

/// <summary>
/// Refreshes the left grid showing the unassigned items, by resetting the range and executing the datasource queries.
/// </summary>
/// <param name="_retainPosition">
/// Defines if the cursor position in the grid will be preserved after the refresh.
/// </param>
public void refreshLeftGrid(boolean _retainPosition = true)
{
    qbdsForecastLineRange.value(ForecastItemAllocation.AllocateId);
    ReqDemPlanEasyItemAllocationUnionView_DS.research(_retainPosition);
    qbdsForecastLineRange.status(RangeStatus::Hidden);
}

public void init()
{
    super();

    var range = SysQuery::findOrCreateRange(this.queryBuildDataSource(),fieldNum(ForecastItemAllocation, AllocateId));
    range.Value(itemAllocation.AllocateId);
    range.status(RangeStatus::Hidden);
}

        public void init()
        {
            QueryBuildDataSource    qbdsLine;
            QueryBuildDataSource    qbdsUnion;

            super();

            qbdsLine = this.queryBuildDataSource().addDataSource(tableNum(ReqDemPlanEasyItemAllocationLineView));
            qbdsLine.addLink(fieldNum(ReqDemPlanEasyItemAllocationUnionView, ItemId), fieldNum(ReqDemPlanEasyItemAllocationLineView, ItemId));
            qbdsLine.addLink(fieldNum(ReqDemPlanEasyItemAllocationUnionView, inventColorId), fieldNum(ReqDemPlanEasyItemAllocationLineView, inventColorId));
            qbdsLine.addLink(fieldNum(ReqDemPlanEasyItemAllocationUnionView, inventSizeId), fieldNum(ReqDemPlanEasyItemAllocationLineView, inventSizeId));
            qbdsLine.addLink(fieldNum(ReqDemPlanEasyItemAllocationUnionView, inventStyleId), fieldNum(ReqDemPlanEasyItemAllocationLineView, inventStyleId));
			qbdsLine.addLink(fieldNum(ReqDemPlanEasyItemAllocationUnionView, inventVersionId), fieldNum(ReqDemPlanEasyItemAllocationLineView, inventVersionId));
            qbdsLine.addLink(fieldNum(ReqDemPlanEasyItemAllocationUnionView, configId), fieldNum(ReqDemPlanEasyItemAllocationLineView, configId));
            qbdsForecastLineRange = qbdsLine.addRange(fieldNum(ReqDemPlanEasyItemAllocationLineView, AllocationId));
            qbdsForecastLineRange.value(itemAllocation.AllocateId);
            qbdsForecastLineRange.status(RangeStatus::Hidden);
            qbdsLine.joinMode(JoinMode::NoExistsJoin);

            qbdsUnion = this.query().dataSourceTable(tablenum(ReqDemPlanEasyItemAllocationUnionView));
            qbdsUnion.addSortField(fieldnum(ReqDemPlanEasyItemAllocationUnionView, ItemId), SortOrder::Ascending);
            qbdsUnion.addSortField(fieldnum(ReqDemPlanEasyItemAllocationUnionView, configId), SortOrder::Ascending);
            qbdsUnion.addSortField(fieldnum(ReqDemPlanEasyItemAllocationUnionView, inventSizeId), SortOrder::Ascending);
            qbdsUnion.addSortField(fieldnum(ReqDemPlanEasyItemAllocationUnionView, inventColorId), SortOrder::Ascending);
            qbdsUnion.addSortField(fieldnum(ReqDemPlanEasyItemAllocationUnionView, inventStyleId), SortOrder::Ascending);
			qbdsUnion.addSortField(fieldnum(ReqDemPlanEasyItemAllocationUnionView, inventVersionId), SortOrder::Ascending);
        }

public void init()
{
    super();

    this.query()
        .dataSourceTable(tablenum(ForecastItemAllocationLine))
        .addSortField(fieldnum(ForecastItemAllocationLine, ItemId), SortOrder::Ascending);
}

/// <summary>
/// Does a research operation on the datasource.
/// </summary>
/// <param name="_retainPosition">
/// Defines if the cursor position in the grid will be preserved after the refresh.
/// </param>
public void research(boolean _retainPosition = true)
{
    super(_retainPosition);

    element.refreshLeftGrid(_retainPosition);
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tablenum(InventDim));
    InventDim::queryAddSortFields(qbds, InventDim::dimProductDimFieldList());
}
