public class ReqDemPlanForecastParameters extends FormRun
{
    ReqDemplanForecastIAKParameters       iakParams;
    #ReqDemPlan

    ReqDemPlanForecastAttributeManager    forecastAttributeManager;
    ReqDemPlanForecastAttributeListPanel  listPanel;

}

/// <summary>
/// This method overwrites the init() function to initialize a single row on the parameters table
/// </summary>
public void init()
{
    ReqDemPlanForecastParameters::find();
    ReqDemPlanDefaultAlgorithmParameters::setDefaults();

    forecastAttributeManager = new ReqDemPlanForecastAttributeManager();
    forecastAttributeManager.insertMissingAttributes();

    listPanel = ReqDemPlanForecastAttributeListPanel::newFromForm(element,
                    element.controlId(formControlStr(ReqDemPlanForecastParameters, AttributesSelection)));

    super();

    listPanel.init();

    AzureMlService.visible(ReqDemPlanAzureMachineLearningServiceFeature::isEnabled());

    if (ScpDemandPlanningWhatsNewNotificationFlight::instance().isEnabled())
    {
        SystemNotificationsWhatsNewManager::AddWhatsNewWithActionLink(
                '{1148ca1c-d2d8-4a79-98b2-a46923945e79}',
                "@ParallelizedForecastImport:WhatsNewTitle",
                "@ParallelizedForecastImport:WhatsNewDesc",
                StaticText.name(),
                'https://go.microsoft.com/fwlink/?linkid=2247703');
    }
}

public void run()
{
    super();

    listPanel.fill();

    this.enableCustomStorageAccountFields();
}

/// <summary>
///     This method is used to tie the custom lookup form to the Forecast Item allocation field on the form. This was needed due to the fact
///     that the ForecastItemAllocation table can only be referenced true the item allocation key ID and DataAreaID of the Item allocation key
///     Normal auto lookups or the custom lookups for that matter do not let us take multiple values from the lookup. Thus we create a custom
///     lookup form which will let us take the values directly from the data source.
/// </summary>
/// <param name="_callingControl">
///     The calling form control.
/// </param>
public void lookupItemAllocationKey(FormStringControl _callingControl)
{
    ForecastItemAllocation  selectedRecord;
    Args                    args;
    FormRun                 formRun;
    Form                    workerLookupForm = new Form(formStr(ReqDemPlanForecastIAKLookup));
    FormControl             control = _callingControl;
    FormStringControl       stringControl       = control as FormStringControl;

    args = new Args();
    args.name(formStr(ReqDemPlanForecastIAKLookup));
    args.caller(_callingControl);

    args.parmObject(_callingControl);

    // position the lookup to highlight the current record
    args.lookupRecord(selectedRecord);

    // perform form lookup on the caller form control
    formRun = FormAutoLookupFactory::buildLookupFromCustomForm(stringControl, workerLookupForm , AbsoluteFieldBinding::construct(fieldStr(ForecastItemAllocation, AllocateId), tableStr(ForecastItemAllocation)), args);
    stringControl.performFormLookup(formRun);
}

/// <summary>
///     Returns a boolean value indicating whether the server supports multiple statistical forecasting models per item allocation keys.
/// </summary>
/// <param name="_rulingForecastingStrategy">
///     The ruling strategy used in the current context.
/// </param>
/// <returns>
///     True if the server supports multiple statistical forecasting models per item allocations keys, otherwise false.
/// </returns>
private boolean isForecastingPerIAKSupported(ReqDemPlanForecastingStrategy _rulingForecastingStrategy)
{
    return _rulingForecastingStrategy == ReqDemPlanForecastingStrategy::AzureMachineLearning
        || _rulingForecastingStrategy == ReqDemPlanForecastingStrategy::MachineLearningService;
}

/// <summary>
///     Looks up usable references from the <c>ReqDemPlanDefaultAlgorithmParameters</c> table based on the specified parameters.
/// </summary>
/// <param name="_formReferenceControl">
///     The control executing the lookup.
/// </param>
/// <param name="_rulingForecastingStrategy">
///     The ruling strategy used in the current context.
/// </param>
/// <returns>
///     The cursor used in the query lookup.
/// </returns>
public Common parameterLookupReference(FormReferenceControl _formReferenceControl, ReqDemPlanForecastingStrategy _rulingForecastingStrategy)
{
    SysReferenceTableLookup               sysReferenceTableLookup;

    sysReferenceTableLookup = SysReferenceTableLookup::newParameters(tableNum(ReqDemPlanDefaultAlgorithmParameters), _formReferenceControl, true);

    sysReferenceTableLookup.parmQuery(ReqDemPlanDefaultAlgorithmParameters::queryLookupIAKParameters(this.isForecastingPerIAKSupported(_rulingForecastingStrategy)));
    sysReferenceTableLookup.addLookupfield(fieldNum(ReqDemPlanDefaultAlgorithmParameters, FriendlyName));
    sysReferenceTableLookup.addLookupfield(fieldNum(ReqDemPlanDefaultAlgorithmParameters, ValueDescription));

    return sysReferenceTableLookup.performFormLookup();
}

public boolean validateIAK(FormStringControl _callingControl)
{
    // This is called once the validate on the field is called after selecting the value. We lookup the current cursor position on the
    // Data source and select the value there
    ReqDemPlanForecastIAKMap iaKmap = reqDemPlanForecastIAKMap_DS.cursor();

    // We copy the values from the cursor row to the fields on the table
    iaKmap.ForecastItemAllocationDataAreaID = iakCompany.text();
    iaKmap.ForecastItemAllocation = _callingControl.text();

    return true;
}

/// <summary>
/// Enables or disables the fields for entering a custom storage account information.
/// </summary>
public void enableCustomStorageAccountFields()
{
    AzureMlBody_AzureStorageAccountKey.enabled(ReqDemPlanForecastParameters.UseCustomAzureStorage);
    AzureMlBody_AzureStorageAccountName.enabled(ReqDemPlanForecastParameters.UseCustomAzureStorage);
}

private void openMoreInformationForMLSUrl()
{
    var openInNewTab = true;
    var showExitWarning = false;

    new Browser().navigate('https://go.microsoft.com/fwlink/?linkid=2165514', openInNewTab, showExitWarning);
}

